INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (36,'ARMOR','Armure matelassée','L''armure matelassée se compose de couches matelassées de tissu et ouate.',8,0,1,1,5,1,'2016-09-15 08:07:17','2016-09-15 08:07:17');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (37,'ARMOR','Armure de cuir','Le plastron et les épaules protecteurs de cette armure sont faits de cuir qui a été solidifié en étant bouilli dans de l''huile. Le reste de l''armure est constituée de matériaux plus doux et plus souples.',10,0,1,1,10,1,'2016-09-15 08:07:39','2016-09-15 08:07:39');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (38,'ARMOR','Armure de cuir clouté','Fabriqué à partir de cuir solide mais flexible, l''armure de cuir clouté est renforcée avec des rivets ou des crampons.',13,0,1,1,45,1,'2016-09-15 08:08:01','2016-09-15 08:08:01');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (39,'ARMOR','Armure de peau','Cette armure brute se compose de fourrures épaisses et de peaux. Il est communément porté par les tribus barbares , les humanoïdes mauvais et d''autres gens qui n''ont pas accès aux outils et les matériaux nécessaires pour créer une meilleure armure.',12,0,1,1,10,1,'2016-09-15 08:08:19','2016-09-15 08:08:19');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (40,'ARMOR','Chemise de cotte de maille','Fait d''anneaux métalliques entrelacées, une chemise de cotte de maille est portée entre les couches de vêtements ou de cuir . Cette armure offre une protection modeste supérieure au porteur et permet au son des mailles d''être étouffé par les couches extérieures.',20,0,1,1,50,1,'2016-09-15 08:08:46','2016-09-15 08:08:46');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (41,'ARMOR','Armure feuilletée','Cette armure se compose d''un manteau et des jambières de cuir recouvert de chevauchement des morceaux de métal, un peu comme les écailles d''un poisson. Le costume comprend gantelets.',45,0,1,1,50,1,'2016-09-15 08:09:07','2016-09-15 08:09:07');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (42,'ARMOR','Plastron de plate','Cette armure est constituée d''un métal ajusté à la poitrine porté avec cuir souple . Bien qu''elle laisse les jambes et les bras relativement non protégés, cette armure fournit une bonne protection pour les organes vitaux tout en laissant le porteur relativement non-encombré.',20,0,1,1,400,1,'2016-09-15 08:09:39','2016-09-15 08:09:39');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (43,'ARMOR','Armure demie-plate','La demie-plate est constituée de plaques métalliques profilées qui couvrent la majeure partie du corps de l''utilisateur . Il ne comprend pas de protection aux jambes sauf des jambières simples qui sont attachées\navec des lanières de cuir .',40,0,1,1,750,1,'2016-09-15 08:10:01','2016-09-15 08:10:01');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (44,'ARMOR','Armure annelée','Cette armure est une armure de cuir avec de lourdes anneaux cousus en elle. Les anneaux contribuent à renforcer l''armure contre les coups de sabres et des haches. L''armure annelée est inférieure à la cotte de maille et est généralement portée que par ceux qui ne peuvent pas se permettre une meilleure armure.',40,0,1,1,30,1,'2016-09-15 08:10:19','2016-09-15 08:10:19');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (45,'ARMOR','Cotte de maille','Fait d''anneaux métalliques entrelacés , la cotte de maille contient une couche de tissu matelassé portée sous\nles mailles pour éviter les frottements et à amortir l''impact des coups . Le costume comprend gantelets.',55,0,1,1,75,1,'2016-09-15 08:10:42','2016-09-15 08:10:42');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (46,'ARMOR','Armure à éclisses','Cette armure est faite de bandes verticales étroites de métal riveté sur un support de cuir qui est porté sur un\nrembourrage en tissu. Une cotte de mailles flexible protège les articulations.',60,0,1,1,200,1,'2016-09-15 08:11:02','2016-09-15 08:11:02');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (47,'ARMOR','Armure de plate complète','L''armure de plate complète est formée de plaques de métal entremêlées pour couvrir l''ensemble du corps. Une armure de plate comprend gantelets, des bottes en cuir épais, un casque à visière et une épaisse couche de rembourrage sous l''armure . Boucles et sangles distribuent le poids sur le corps.',65,0,1,1,1500,1,'2016-09-15 08:11:24','2016-09-15 08:11:24');
