INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (143,'AMMUNITION','Carreaux d''arbalète (20)','Des carreaux servant à l''arbalète de poing, légère ou lourde.',1.5,0,1,0,1,2,'2016-09-15 10:20:57','2016-09-15 10:21:45');
