INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (1,'Acteur','Entraîné au mimétisme et à l''art dramatique, vous gagnez les avantages suivants : \n• Augmentez votre valeur de Charisme de 1, pour un maximum de 20. \n• Vous avez l''avantage aux jets de Charisme (Tromperie) et Charisme (Représentation) pour essayer de vous faire passer pour une autre personne. \n• Vous pouvez imiter le discours d''une autre personne ou les sons émis par d''autres créatures. Vous devez avoir entendu la personne qui parle, ou entendu la créature émettre le son, pendant au moins 1 minute. Un jet réussi de Sagesse (Intuition) contre votre jet de Charisme (Duperie) permet à celui qui écoute de déterminer qu''il s''agit d''une imitation. ','PASSIVE',0,'NONE',NULL,NULL,NULL,3,'2016-09-13 11:14:39','2016-09-13 11:23:47');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (3,'Adepte élémentaire feu','Les sorts que vous lancez ignorent la résistance aux dégâts de feu. En outre, lorsque vous lancez les dés de dégâts pour un sort que vous avez lancé et qui inflige des dégâts de feu, vous pouvez traiter tout 1 aux dés comme un 2. ','PASSIVE',0,'CAST_SPELL',NULL,NULL,NULL,4,'2016-09-13 11:21:18','2016-09-13 13:19:26');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (4,'Adepte élémentaire froid','Les sorts que vous lancez ignorent la résistance aux dégâts de froid. En outre, lorsque vous lancez les dés de dégâts pour un sort que vous avez lancé et qui inflige des dégâts de froid, vous pouvez traiter tout 1 aux dés comme un 2. ','PASSIVE',0,'CAST_SPELL',NULL,NULL,NULL,2,'2016-09-13 11:27:51','2016-09-13 13:19:31');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (5,'Adepte élémentaire électricité','Les sorts que vous lancez ignorent la résistance aux dégâts d''électricité. En outre, lorsque vous lancez les dés de dégâts pour un sort que vous avez lancé et qui inflige des dégâts d''électricité, vous pouvez traiter tout 1 aux dés comme un 2. ','PASSIVE',0,'CAST_SPELL',NULL,NULL,NULL,2,'2016-09-13 11:28:33','2016-09-13 13:19:35');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (6,'Adepte élémentaire tonnerre','Les sorts que vous lancez ignorent la résistance aux dégâts de tonnerre. En outre, lorsque vous lancez les dés de dégâts pour un sort que vous avez lancé et qui inflige des dégâts d''électricité, vous pouvez traiter tout 1 aux dés comme un 2. ','PASSIVE',0,'CAST_SPELL',NULL,NULL,NULL,2,'2016-09-13 11:29:30','2016-09-13 13:19:38');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (7,'Armure légère','Vous avez été formé pour maîtriser l''utilisation des armures légères, et vous bénéficiez des avantages suivants : \n• Augmentez votre valeur de Force ou de Dextérité de 1, pour un maximum de 20, \n• Vous gagnez la maîtrise des armures légères. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 11:39:16','2016-09-13 11:39:16');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (8,'Armure intermédiaire','Vous avez été formé pour maîtriser l''utilisation des armures intermédiaires et des boucliers, et vous bénéficiez des avantages suivants : \n• Augmentez votre valeur de Force ou de Dextérité de 1, pour un maximum de 20, \n• Vous gagnez la maîtrise des armures intermédiaires et des boucliers. ','PASSIVE',0,'ARMOR_PROFICIENCY','LIGHT',NULL,NULL,2,'2016-09-13 11:41:12','2016-09-13 13:19:51');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (9,'Armure lourde','Vous avez été formé pour maîtriser l''utilisation des armures lourdes, et vous bénéficiez des avantages suivants : \n• Augmentez votre valeur de Force de 1, pour un maximum de 20, \n• Vous gagnez la maîtrise des armures lourdes. ','PASSIVE',0,'ARMOR_PROFICIENCY','MEDIUM',NULL,NULL,1,'2016-09-13 13:20:34','2016-09-13 13:20:34');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (10,'Athlète','Vous avez suivi une forte formation physique qui vous donne les avantages suivants : \n• Augmentez votre valeur de Force ou de Dextérité de 1, pour un maximum de 20. \n• Lorsque vous êtes à terre, vous relever prend seulement 5 pieds de votre mouvement. \n• Escalader ne vous coûte pas de mouvement supplémentaire. \n• Vous pouvez faire un saut en longueur ou en hauteur avec seulement 5 pieds d''élan, au lieu de 10 pieds. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:22:03','2016-09-13 13:22:03');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (11,'Attaquant sauvage','Une fois par tour quand vous lancez les dégâts d''une attaque avec une arme de corps à corps, vous pouvez relancer les dégâts de l''arme et choisir le meilleur des deux résultats. ','ACTION',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:23:26','2016-09-13 13:23:26');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (12,'Bagarreur de tavernes','Habitué à vous battre comme un chiffonnier en utilisant tout ce qui vous tombe sous la main, vous recevez les bénéfices suivants : \n• Augmentez votre valeur de Force ou de Constitution de 1, sans dépasser 20. \n• Vous maîtrisez les armes improvisées. \n• Votre attaque à mains nues utilise 1d4 pour les dégâts. \n• Pendant votre tour, si vous touchez une cible avec une attaque à mains nues ou une arme improvisée, vous pouvez utiliser une action bonus pour essayer d''engager une lutte avec la cible. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:24:34','2016-09-13 13:24:34');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (13,'Combattant à 2 armes','Vous maîtrisez le combat à deux armes, et obtenez les avantages suivants : \n• Vous gagnez un bonus de +1 à la CA si vous tenez une arme de corps à corps différente dans chaque main. \n• Vous pouvez utiliser le combat à deux armes même si les armes de corps à corps à une main que vous tenez ne sont pas légères. \n• Vous pouvez dégainer ou rengainer deux armes à une main alors que normalement vous ne pourriez le faire que pour une seule. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:27:46','2016-09-13 13:27:46');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (14,'Duelliste défensif','Lorsque vous tenez une arme de finesse que vous maîtrisez et qu''une autre créature vous touche avec une attaque de corps à corps, vous pouvez utiliser votre réaction pour ajouter votre bonus de maîtrise à votre CA pour cette attaque, ce qui pourrait faire que l''attaque vous rate. ','REACTION',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:29:47','2016-09-13 13:29:47');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (15,'Endurant','Robuste et résistant, vous gagnez les avantages suivants : \n• Augmentez votre valeur de Constitution de 1, jusqu''à un maximum de 20. \n• Lorsque vous jetez un Dé de vie pour regagner des points de vie, le nombre minimum de points de vie que vous regagnez de ce jet est égal à deux fois votre modificateur de Constitution (minimum 2). ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:31:18','2016-09-13 13:31:18');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (16,'Esprit vif','Vous avez un esprit qui permet de repérer le temps, les directions et les détails avec une précision étonnante. Vous obtenez les avantages suivants : \n• Augmentez votre valeur d''Intelligence de 1, jusqu''à un maximum de 20. \n• Vous savez toujours où est le nord. \n• Vous savez toujours le nombre d''heures restantes avant le lever du soleil suivant ou le coucher du soleil. \n• Vous pouvez vous rappeler avec précision ce que vous avez vu ou entendu au cours du mois passé. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:31:50','2016-09-13 13:31:50');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (17,'Expert en arbalètes','Grâce à votre longue pratique de l''arbalète, vous gagnez les avantages suivants : \n• Vous ignorez la propriété chargement des arbalètes de poing et des arbalètes légères que vous maîtrisez. \n• Être à 5 pieds ou moins d''une créature hostile ne donne pas un désavantage à vos jets d''attaque à distance. \n• Lorsque vous utilisez l''action Attaquer et attaquez avec une arme à une main, vous pouvez utiliser une action bonus pour attaquer avec une arbalète de poing si vous en tenez une. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:33:09','2016-09-13 13:33:09');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (18,'Explorateur de donjons','Vigilant face aux pièges cachés et portes secrètes que l''on trouve dans de nombreux donjons, vous obtenez les avantages suivants : \n• Vous avez l''avantage aux jets de Sagesse (Perception) et Intelligence (Investigation) effectués pour détecter la présence de portes secrètes. \n• Vous avez avantage aux jets de sauvegarde pour éviter ou résister aux pièges. \n• Vous obtenez la résistance aux dégâts infligés par les pièges. \n• Vous pouvez rechercher des pièges tout en vous déplaçant à un rythme normal, au lieu d''un rythme lent. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:33:51','2016-09-13 13:33:51');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (19,'Fonceur','Lorsque vous utilisez votre action de Courir, vous pouvez utiliser une action bonus pour faire une attaque avec une arme de corps à corps ou pousser une créature. \nSi vous vous déplacez d''au moins 10 pieds en ligne droite juste avant de prendre cette action bonus, vous gagnez soit un bonus de +5 aux dégâts de l''attaque (si vous avez choisi de faire une attaque de corps à corps et que vous touchez), soit vous repoussez la cible jusqu''à 10 pieds de vous (si vous avez choisi de la pousser et que vous réussissez). ','ACTION',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:35:00','2016-09-13 13:35:00');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (20,'Furtif','Vous êtes expert pour vous faufiler discrètement dans l''ombre. Vous obtenez les avantages suivants : \n• Vous pouvez essayer de vous cacher d''une créature quand vous êtes dans une zone dont la visibilité est réduite pour elle. \n• Lorsque vous êtes caché d''une créature et que vous ratez une attaque avec une arme à distance, l''attaque ne révèle pas votre position. \n• Une lumière faible n''impose pas un désavantage à vos jets de Sagesse (Perception) en relation avec la vue. ','PASSIVE',0,'ABILITY',NULL,2,13,1,'2016-09-13 13:36:14','2016-09-13 13:36:14');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (21,'Guérisseur','Vous êtes un habile médecin, ce qui vous permet de guérir les blessures rapidement pour que vos alliés puissent retourner au combat. Vous gagnez les avantages suivants : \n• Lorsque vous utilisez une trousse de soins pour stabiliser une créature mourante, cette créature regagne également 1 point de vie. \n• Par une action, vous pouvez dépenser une utilisation d''une trousse de soins pour soigner une créature et lui redonner 1d6 + 4 points de vie, plus un nombre de points de vie supplémentaires égal au nombre maximum de Dés de vie de la créature. La créature ne peut pas bénéficier de vos soins de nouveau jusqu''à ce qu''elle termine un repos court ou long. ','ACTION',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:37:25','2016-09-13 13:37:25');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (22,'Lanceur de sorts d''attaque','Vous avez appris des techniques pour améliorer vos attaques avec certains types de sorts, et vous obtenez les avantages suivants : \n• Lorsque vous lancez un sort qui vous oblige à faire un jet d''attaque, la portée du sort est doublé. \n• Vos attaques de sort à distance ignorent les abris partiels et les abris importants. ','PASSIVE',0,'CAST_SPELL',NULL,NULL,NULL,1,'2016-09-13 13:39:57','2016-09-13 13:39:57');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (23,'Lanceur de sorts de bataille','Vous avez pratiqué le lancer des sorts au milieu d''un combat, apprenant des techniques que vous accordent les avantages suivants : \n• Vous avez l''avantage aux jets de sauvegarde de Constitution pour maintenir votre concentration sur un sort lorsque vous subissez des dégâts. \n• Vous pouvez effectuer les composantes somatiques d''un sort même si vous avez des armes ou un bouclier dans les mains. \n• Lorsque le mouvement d''une créature hostile provoque une attaque d''opportunité pour vous, vous pouvez utiliser votre réaction pour lancer un sort à la créature, plutôt que de faire une attaque d''opportunité. Le sort doit avoir un temps d''incantation de 1 action et ne doit viser que cette créature. ','PASSIVE',0,'CAST_SPELL',NULL,NULL,NULL,1,'2016-09-13 13:40:49','2016-09-13 13:40:49');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (24,'Leader inspirant','Vous pouvez passer 10 minutes à inspirer vos compagnons, confortant leur détermination à se battre. Lorsque vous faites cela, choisissez jusqu''à six créatures amicales (vous pouvez en faire partie) dans un rayon de 9 mètres autour de vous, qui peuvent vous voir ou vous entendre, et qui peuvent vous comprendre. Chaque créature peut gagner un nombre de points de vie temporaires égal à votre niveau + votre modificateur de Charisme. Une créature ne peut pas gagner de nouveaux points de vie temporaires grâce à ce don jusqu''à ce qu''elle ait fini un repos de court ou long. ','ACTION',0,'ABILITY',NULL,6,13,1,'2016-09-13 13:41:34','2016-09-13 13:41:34');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (25,'Linguiste','Vous avez étudié les langues et les codes, et bénéficiez des avantages suivants : \n• Augmentez votre valeur d''Intelligence de 1, jusqu''à un maximum de 20. \n• Vous apprenez trois langues de votre choix. \n• Vous pouvez coder des messages avec compétence. D''autres personnes ne peuvent déchiffrer un code que vous avez créé à moins que vous le leur ayez enseigné, qu''elles réussissent un jet d''Intelligence (DD égal à votre valeur d''Intelligence + votre bonus de maîtrise) ou qu''elles utilisent la magie pour le déchiffrer. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:42:18','2016-09-13 13:42:18');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (26,'Lutteur','Vous avez développé les compétences nécessaires pour vous défendre tout seul dans les combats de rues. Vous obtenez les avantages suivants : \n• Vous avez l''avantage aux jets d''attaque contre une créature avec laquelle vous êtes engagé dans une lutte. \n• Vous pouvez utiliser votre action pour tenter de bloquer une créature avec qui vous avez engagé une lutte. Dans ce cas, faites un nouveau jet de lutte. Si vous réussissez, vous et la créature êtes tous deux entravés jusqu''à ce que la lutte se termine. ','ACTION',0,'ABILITY',NULL,1,13,1,'2016-09-13 13:43:11','2016-09-13 13:43:11');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (27,'Maitre d''armes','Vous avez énormément pratiqué avec une grande variété d''armes, et obtenez les avantages suivants : \n• Augmentez votre valeur de Force ou de Dextérité de 1, jusqu''à un maximum de 20. \n• Vous gagnez la maîtrise de quatre armes courantes ou de guerre de votre choix. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:43:51','2016-09-13 13:43:51');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (28,'Maitre des armes à 2 mains','Vous avez appris à tirer avantage du poids d''une arme, laissant son élan augmenter l''efficacité de vos frappes. Vous obtenez les avantages suivants : \n• A votre tour, lorsque vous réalisez un coup critique avec une arme de corps à corps ou réduisez une créature à 0 point de vie avec ce type d''arme, vous pouvez réaliser une attaque avec une arme de corps à corps comme action bonus. \n• Avant de faire une attaque de corps à corps avec une arme lourde que vous maîtrisez, vous pouvez choisir de prendre un malus de -5 au jet d''attaque. Si l''attaque réussit, vous ajoutez +10 aux dégâts de l''attaque. ','ACTION',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:46:30','2016-09-13 13:46:30');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (29,'Maitre des armes d''hast','Vous pouvez garder vos ennemis à distance avec des armes à allonge. Vous obtenez les avantages suivants : \n• Lorsque vous prenez l''action Attaquer et attaquez avec seulement une pique, une hallebarde ou un bâton, vous pouvez utiliser une action bonus pour effectuer une attaque au corps à corps avec l''extrémité opposée de l''arme. Le dé de dégâts pour cette attaque est 1d4, et l''attaque inflige des dégâts contondants. \n• Lorsque vous portez une hallebarde, une pique ou un bâton, vous pouvez effectuer une attaque d''opportunité lorsqu''une créature entre à portée de votre allonge. ','ACTION',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 13:58:40','2016-09-13 13:58:40');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (30,'Maitre des armure intermédiaires','Vous avez pratiqué le déplacement avec une armure intermédiaire pour obtenir les avantages suivants : \n• Porter une armure intermédiaire n''impose pas de désavantage à vos jets de Dextérité (Discrétion). \n• Lorsque vous portez une armure intermédiaire, vous pouvez ajouter 3, au lieu de 2, à votre CA si vous avez une Dextérité de 16 ou plus. ','PASSIVE',0,'ARMOR_PROFICIENCY','MEDIUM',NULL,NULL,1,'2016-09-13 14:00:24','2016-09-13 14:00:24');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (31,'Maitre des armures lourdes','Vous pouvez utiliser votre armure pour détourner des attaques qui en auraient tué plus d''un. Vous obtenez les avantages suivants : \n• Augmentez votre valeur de Force de 1, pour un maximum de 20. \n• Lorsque vous portez une armure lourde, les dégâts contondants, perforants et tranchants que vous recevez d''armes non magiques sont réduits de 3. ','PASSIVE',0,'ARMOR_PROFICIENCY','HEAVY',NULL,NULL,1,'2016-09-13 14:01:01','2016-09-13 14:01:01');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (32,'Maitre des boucliers','Vous utilisez les boucliers non seulement pour vous protéger, mais aussi de manière offensive. Vous obtenez les avantages suivants lorsque vous portez un bouclier : \n• Si vous prenez l''action Attaquer à votre tour, vous pouvez utiliser une action bonus pour essayer de bousculer une créature située à 5 pieds ou moins de vous avec votre bouclier. \n• Si vous n''êtes pas incapable d''agir, vous pouvez ajouter le bonus à la CA de votre bouclier à tout jet de sauvegarde de Dextérité contre tout sort ou autre effet nocif qui vous prend pour cible. \n• Si vous êtes soumis à un effet qui vous permet de faire un jet de sauvegarde de Dextérité pour ne prendre que la moitié des dégâts, vous pouvez utiliser votre réaction pour ne prendre aucun dommage si vous réussissez le jet de sauvegarde, en interposant votre bouclier entre vous et la source de l''effet. ','ACTION',0,'ARMOR_PROFICIENCY','SHIELD',NULL,NULL,1,'2016-09-13 14:03:36','2016-09-13 14:03:36');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (33,'Mobile','Vous êtes exceptionnellement rapide. Vous gagnez les avantages suivants : \n• Votre vitesse augmente de 3 mètres. \n• Lorsque vous utilisez l''action de Courir, un terrain difficile ne vous coûte pas de mouvement supplémentaire pour ce tour. \n• Lorsque vous effectuez une attaque au corps à corps contre une créature, vous ne provoquez pas d''attaques d''opportunité pour cette créature pour le reste du tour, que vous touchiez ou pas. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 14:04:14','2016-09-13 14:04:14');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (34,'Observateur','Vous notez rapidement des détails dans votre environnement. Vous gagnez les avantages suivants : \n• Augmentez votre valeur d''Intelligence ou de Sagesse de 1, pour un maximum de 20. \n• Si vous pouvez voir la bouche d''une créature alors qu''elle parle une langue que vous comprenez, vous pouvez interpréter ce qu''elle dit en lisant ses lèvres. \n• Vous avez un bonus de +5 à vos jets de Sagesse (Perception) passive et d''Intelligence (Investigation) passive. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 14:04:46','2016-09-13 14:04:46');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (35,'Pourfendeur de mage','Vous avez pratiqué des techniques utiles en combat au corps à corps contre les lanceurs de sorts, et vous bénéficiez des avantages suivants : \n• Quand une créature dans un rayon de 5 pieds autour de vous lance un sort, vous pouvez utiliser votre réaction pour effectuer une attaque avec une arme au corps à corps contre cette créature. \n• Si vous touchez une créature qui se concentre sur un sort, cette créature a un désavantage à son jet de sauvegarde pour maintenir sa concentration. \n• Vous avez l''avantage aux jets de sauvegarde contre les sorts lancés par les créatures dans un rayon de 1,50 m autour de vous. ','REACTION',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 14:05:32','2016-09-13 14:05:32');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (36,'Résistant','Choisissez une caractéristique. Vous gagnez les avantages suivants : \n• Augmentez la valeur de cette caractéristique de 1, pour un maximum de 20. \n• Vous gagnez la maîtrise des jets de sauvegarde qui utilisent la caractéristique choisie. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 14:07:33','2016-09-13 14:07:33');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (37,'Robuste','Vos points de vie maximums augmentent d''un montant égal au double de votre niveau quand vous gagnez ce don. Chaque fois que vous gagnez un niveau par la suite, vos points de vie maximums augmentent de 2 points de vie supplémentaires. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 14:09:11','2016-09-13 14:09:11');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (38,'Sentinelle','Vous maîtrisez les techniques pour tirer avantage de chaque faiblesse dans la garde de tout ennemi, et bénéficiez des avantages suivants : \n• Lorsque vous touchez une créature avec une attaque d''opportunité, la vitesse de la créature devient 0 pour le reste du tour. \n• Vous pouvez effectuer une attaque d''opportunité même si une créature prend l''action Se désengager avant de se mettre hors de portée de votre allonge. \n• Quand une créature à 1,50 mètre ou moins de vous fait une attaque contre une cible autre que vous (et que cette cible n''a pas ce don), vous pouvez utiliser votre réaction pour réaliser une attaque avec une arme de corps à corps contre la créature attaquante. ','ACTION',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 14:10:02','2016-09-13 14:10:02');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (39,'Talentueux','Vous gagnez la maîtrise de trois compétences ou outils de votre choix, avec la combinaison que vous souhaitez. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 14:10:35','2016-09-13 14:10:35');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (40,'Tireur d''élite','Vous maîtrisez les armes à distance et pouvez faire des tirs que d''autres penseraient impossible. Vous obtenez les avantages suivants : \n• Attaquer à longue portée n''impose pas de désavantage à vos jets d''attaque avec une arme à distance. \n• Vos attaques avec des armes à distance ignorent les abris partiels et les abris importants. \n• Avant de faire une attaque avec une arme à distance que vous maîtrisez, vous pouvez choisir de prendre un malus de -5 au jet d''attaque. Si l''attaque réussit, vous ajoutez +10 aux dégâts de l''attaque. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 14:11:28','2016-09-13 14:11:28');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, version, created, updated) VALUES (41,'Vigilant','Toujours en train de guetter le danger, vous gagnez les bénéfices suivants : \n• Vous gagnez un bonus de +5 à l''initiative. \n• Vous ne pouvez pas être surpris tant que vous êtes conscient. \n• Les autres créatures n''ont pas l''avantage aux jets d''attaque contre vous du fait de vous être cachées. ','PASSIVE',0,'NONE',NULL,NULL,NULL,1,'2016-09-13 14:12:09','2016-09-13 14:12:09');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (42,'Rage','En combat, vous vous battez avec une férocité bestiale. Durant votre tour, vous pouvez entrer en rage en utilisant une action bonus. En rage, vous gagnez les bénéfices suivants si vous ne portez pas d''armure lourde :\n\nVous avez l''avantage aux jets de Force et aux jets de sauvegarde de Force.\nQuand vous effectuez une attaque d''arme de corps à corps utilisant la Force, vous gagnez un bonus aux jets de dégâts qui dépend de votre niveau de barbare, comme indiqué dans la colonne Dégâts de la table ci-dessus.\nVous avez la résistance aux dégâts contondants, perforants et tranchants.\nSi vous êtes capable de lancer des sorts, vous ne pouvez les lancer ou vous concentrer sur eux pour toute la durée de la rage.\n\nVotre rage dure 1 minute. Elle finit prématurément si vous devenez inconscient, ou si votre tour se termine et que vous n''avez ni attaqué une créature hostile, ni subi des dégâts, depuis votre précédent tour. Vous pouvez également mettre fin à votre rage durant votre tour par une action bonus. Vous récupérez les utilisations de rage dépensées après avoir terminé un repos long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,'LONG',NULL,2,'2016-09-13 14:18:52','2016-12-04 11:17:03');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (43,'Défense sans armure','Tant que vous ne portez pas d''armure, votre classe d''armure est égale à 10 + votre modificateur de Dextérité + votre modificateur de Constitution. Vous pouvez utiliser un bouclier et en gagner les avantages.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-13 14:19:24','2016-09-13 14:19:24');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (44,'Attaque téméraire','Vous pouvez mettre de côté votre défense pour attaquer avec toute la violence du désespoir. Lorsque vous effectuez la première attaque de votre tour, vous pouvez décider d''effectuer une Attaque téméraire. Vous obtenez ainsi un avantage aux jets d''attaque d''arme de corps à corps utilisant la Force durant ce tour, mais les attaques effectuées contre vous ont également l''avantage jusqu''à votre prochain tour de jeu.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-13 14:20:08','2016-09-13 14:20:08');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (45,'Sens du danger','Vous ressentez une sensation étrange lorsque les choses qui vous entourent ne sont pas comme elles devraient être, vous donnant un avantage lorsque vous tenter de vous extirper du danger. Vous avez l''avantage aux jets de sauvegarde de Dextérité contre les effets que vous pouvez voir, comme les pièges ou les sorts. Pour bénéficier de cet effet vous ne devez pas être aveuglé, assourdi ou incapable d''agir.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-13 14:20:42','2016-09-13 14:20:42');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (46,'Attaque supplémentaire','Vous pouvez attaquer deux fois, au lieu d''une seule, chaque fois que vous réalisez l’action Attaquer durant votre tour.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-13 14:23:43','2016-09-13 14:23:43');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (47,'Mouvement rapide','Votre vitesse augmente de 10 pieds tant que vous ne portez pas d''armure lourde.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-13 14:24:26','2016-09-13 14:24:26');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (48,'Instinct sauvage','Vos instincts sont si aiguisés que vous obtenez l''avantage aux jets d''initiative. De plus, si vous êtes surpris au début du combat et que vous n''êtes pas incapable d''agir, vous pouvez jouer normalement durant votre premier tour, mais seulement si vous entrez en rage avant de faire quoique ce soit d''autre à ce tour.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-13 14:25:15','2016-09-13 14:25:15');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (49,'Critique brutal 1','Vous pouvez lancer un dé de dégâts de votre arme en plus lorsque vous déterminez les dégâts supplémentaires que vous infligez sur un coup critique réussi avec une attaque de corps à corps.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,2,'2016-09-13 14:26:07','2016-10-19 09:21:51');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (50,'Rage implacable','Votre rage vous permet de continuer à combattre en dépit des graves blessures qui vous affectent. Si vous tombez à 0 point de vie pendant votre rage et que vous ne mourrez pas sur le coup, vous pouvez faire un jet de sauvegarde de Constitution DD 10. Si vous le réussissez, vous retournez immédiatement à 1 point de vie. Chaque fois que vous utilisez cette capacité après la première, le DD augmente de 5. Quand vous terminez un repos court ou long le DD retombe à 10.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-13 14:26:53','2016-09-13 14:26:53');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (51,'Rage persistante','Votre rage est si intense qu''elle ne s''arrête prématurément qu''à la condition que vous tombiez inconscient ou que vous choisissiez de l''arrêter.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-13 14:27:24','2016-09-13 14:27:24');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (52,'Puissance indomptable','Si le résultat d''un de vos jets de Force est inférieur à votre valeur de Force, vous pouvez utiliser votre valeur de Force à la place de votre résultat.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-13 14:27:57','2016-09-13 14:27:57');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (53,'Champion primitif','Vous êtes l''incarnation de la puissance du monde sauvage. Vos valeurs de Force et de Constitution augmentent de 4. Votre maximum dans ces valeurs de caractéristique est maintenant de 24.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-13 14:28:30','2016-09-13 14:28:30');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (54,'Frénésie','Vous pouvez choisir de sombrer dans un état de frénésie au cours de votre rage. Si vous le faites, pour la durée de votre rage, vous pouvez effectuer une unique attaque d''arme de corps à corps en utilisant une action bonus à chacun de vos tours après celui-ci. Lorsque votre rage se termine, vous subissez un niveau d''épuisement.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-14 09:23:50','2016-09-14 09:23:50');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (55,'Rage inébranlable','Vous ne pouvez pas être charmé ou effrayé tant que vous êtes en rage. Si vous êtes déjà charmé ou effrayé lorsque vous entrez en rage, l''effet est suspendu le temps de votre rage.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-14 09:24:14','2016-09-14 09:24:14');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (56,'Présence intimidante','Vous pouvez utiliser votre action pour effrayer quelqu''un avec votre présence effrayante. Pour ce faire, choisissez une créature que vous pouvez voir à 30 pieds maximum de vous. Si la créature peut vous voir ou vous entendre, elle doit réussir un jet de sauvegarde de Sagesse (DD égal à 8 + votre bonus de maîtrise + votre modificateur de Charisme) sous peine d''être effrayée par vous jusqu''à la fin de votre prochain tour. Aux tours suivants, vous pouvez utiliser votre action pour augmenter d''un tour supplémentaire la durée de cet effet sur la créature effrayée. Cet effet se termine si la créature finit son tour hors de votre ligne de vue ou qu''elle se trouve à plus de 60 pieds de vous. Si la créature réussit son jet de sauvegarde, vous ne pouvez plus utiliser cette capacité contre elle durant 24 heures.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-14 09:25:21','2016-09-14 09:25:21');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (57,'Représailles','Lorsque vous subissez des dégâts d''une créature située à 5 pieds de vous, vous pouvez utiliser votre réaction pour effectuer une attaque d''arme de corps à corps contre cette créature.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-14 09:25:55','2016-09-14 09:25:55');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (58,'Esprit chercheur','Une voie qui cherche à vous initier au monde naturel, vous affiliant aux bêtes, est faite pour vous. Lorsque vous adoptez cette voie, vous obtenez la capacité de lancer les sorts sens bestial et compréhension animale.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-14 09:27:06','2016-09-14 09:27:06');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (59,'Esprit totem','Lorsque vous adoptez cette voie, vous choisissez un esprit totem et obtenez les avantages associés. Vous devez fabriquer ou obtenir un objet totem - une amulette ou un ornement similaire - constitué d''os, de poils, de griffes, de plumes ou de dents de votre animal totem. Si vous le souhaitez, vous pouvez également obtenir un attribut physique mineur qui rappelle votre esprit totem. Par exemple, si vous avez choisi l''ours comme esprit totem, vous pourriez être incroyablement poilu et avoir la peau épaisse, ou si votre totem est l''aigle, vos yeux pourraient virer au jaune. Votre animal totem devrait être un animal correspondant à l''un de ceux listés ci-dessous ou s''en approchant mais dont l''espèce est plus appropriée à votre pays d''origine (un vautour ou un faucon à la place d''un aigle par exemple).\n\nOurs. Lorsque vous êtes en rage, vous avez la résistance à tous les types de dégâts sauf aux dégâts psychiques. L''esprit de l''ours vous rend suffisamment coriace pour résister à n''importe quel châtiment.\n\nAigle. Lorsque vous êtes en rage et ne portez pas d''armure lourde, les autres créatures ont un désavantage lors des attaques d''opportunité qu''elles effectuent contre vous, et vous pouvez utiliser l''action Courir en tant qu''action bonus lors de votre tour. L''esprit de l''aigle fait de vous un prédateur capable de circuler dans la mêlée avec aisance.\n\nLoup. Lorsque vous êtes en rage, vos alliés ont l''avantage aux jets d''attaques de corps à corps effectués contre toute créature située à 5 pieds de vous et qui vous sont hostiles. L''esprit du loup fait de vous le chef des chasseurs.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-14 09:28:19','2016-09-14 09:28:19');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (60,'Aspect de la bête','Vous obtenez un bénéfice magique dépendant de l''animal totem de votre choix. Vous pouvez choisir le même animal que celui sélectionné au niveau 3 ou en prendre un différent.\n\nOurs. Vous gagnez la puissance de l''ours. Votre capacité de charge (dont votre chargement maximal et votre capacité à soulever des objets) est doublée et vous obtenez l''avantage aux jets de Force effectués pour pousser, soulever, tirer ou briser des objets.\n\nAigle. Vous gagnez la vision de l''aigle. Vous pouvez voir jusqu''à 1,5 km sans difficulté, capable de discerner même les plus fins détails comme si vous regardiez quelque chose à 100 pieds de vous. De plus, une faible luminosité n''impose pas un désavantage à vos jets de Sagesse (Perception).\n\nLoup. Vous gagnez les sens de chasseur d''un loup. Vous pouvez pister les autres créatures lorsque vous voyagez à un rythme rapide et vous pouvez vous déplacer discrètement lorsque vous voyagez à un rythme normal','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-14 09:29:40','2016-09-14 09:29:40');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (61,'Communion avec la nature','Vous pouvez lancer le sort communion avec la nature. Lorsque vous le lancez, une version spirituelle de l''un des animaux que vous avez choisis avec Esprit totem ou Aspect de la bête apparaît et vous donne l''information que vous recherchez.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-14 09:30:28','2016-09-14 09:30:28');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (62,'Lien totémique','vous obtenez un bénéfice magique correspondant à l''animal totem de votre choix. Vous pouvez choisir un animal que vous avez précédemment sélectionné ou en prendre un nouveau.\n\nOurs. Lorsque vous êtes en rage, toute créature située à 5 pieds de vous, et qui vous est hostile, a un désavantage aux jets d''attaque effectué contre une cible autre que vous, ou tout personnage avec cette capacité. Un ennemi est immunisé à cet effet s''il ne peut pas vous voir ou vous entendre ou qu''il ne peut pas être effrayé.\n\nAigle. Lorsque vous êtes en rage, vous obtenez une vitesse de vol égale à votre vitesse actuelle de déplacement à pied. Cette capacité fonctionne uniquement sur de cours déplacements ; vous tombez si vous terminez votre tour dans les airs et que rien d''autre ne vous maintient en hauteur.\n\nLoup. Lorsque vous êtes en rage vous pouvez utiliser une action bonus lors de votre tour pour mettre à terre une créature de taille G ou inférieure que vous avez touchée lors d''une attaque d''arme de corps à corps.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-14 09:31:38','2016-09-14 09:31:38');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (64,'Touche-à-tout','Vous pouvez ajouter la moitié de votre bonus de maîtrise (arrondi au chiffre inférieur) à tout jet de caractéristique qui n''applique pas déjà votre bonus de maîtrise.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,3,'2016-09-19 08:04:34','2016-11-21 08:43:23');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (65,'Chant de repos d6','Vous pouvez utiliser de la musique ou une oraison apaisante lors d''un repos court pour aider à revitaliser vos alliés blessés. Si vous ou toute créature amie qui peut entendre votre représentation récupérez des points de vie à la fin d''un repos court, chacune de ces créatures récupère 1d6 points de vie supplémentaires.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,2,'2016-09-19 08:05:12','2016-11-21 09:01:35');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (66,'Expertise','Choisissez deux compétences que vous maîtrisez. Votre bonus de maîtrise est doublé pour tout jet de caractéristique que vous faites et qui implique une de ces deux compétences. Au niveau 10, vous pouvez choisir deux autres compétences maîtrisées et obtenir le même avantage.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:05:51','2016-09-19 08:05:51');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (67,'Source d''inspiration','Vous regagnez vos Inspirations bardiques utilisées lorsque vous terminez un repos court ou long.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:06:21','2016-09-19 08:06:21');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (68,'Contre charme','Vous gagnez la possibilité d''utiliser des notes de musique ou des mots de pouvoir pour perturber les effets qui affectent la pensée. Par une action, vous pouvez commencer une représentation qui durera jusqu''à la fin de votre prochain tour. Pendant ce temps, vous et toute créature amie dans un rayon de 30 pieds autour de vous avez avantage aux jets de sauvegarde contre le fait d''être effrayé ou charmé. Une créature doit être en mesure de vous entendre pour obtenir cet avantage. La représentation se termine plus tôt si vous êtes incapable d''agir ou réduit au silence, ou si vous y mettez volontairement un terme (aucune action n''est requise pour cela).','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:07:10','2016-09-19 08:07:10');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (69,'Secrets magiques','Vous avez récupéré des connaissances magiques à partir d''un large éventail de disciplines. Choisissez deux sorts de n''importe quelle classe, y compris de barde. Les sorts choisis doivent être d''un niveau que vous pouvez lancer, comme le montre la table du barde, ou être des sorts mineurs. Les sorts choisis comptent comme des sorts de barde pour vous, et comptent dans la colonne des Sorts connus de la table du barde.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,2,'2016-09-19 08:08:30','2016-11-21 09:05:32');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (70,'Inspiration supérieure','Quand vous jetez l''initiative et n''avez plus d''Inspiration bardique, vous regagnez une utilisation.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:08:51','2016-09-19 08:08:51');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (71,'Maîtrises additionnelles (compétence)','Lorsque vous rejoignez le collège des Connaissances, vous gagnez la maîtrise des trois compétences de votre choix.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,2,'2016-09-19 08:09:31','2016-09-19 08:12:24');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (72,'Mots tranchants','Vous apprenez à utiliser votre esprit pour détourner l''attention ou semer la confusion, ainsi qu''à saper la confiance et la compétence des autres. Quand une créature que vous pouvez voir dans un rayon de 60 pieds autour de vous fait un jet d''attaque, de caractéristique ou de dégâts, vous pouvez utiliser votre réaction pour dépenser une utilisation d''Inspiration bardique et soustraire votre jet du résultat de la cible. Vous pouvez choisir d''utiliser cette capacité après que la créature ait fait son jet, mais vous devez le faire avant que le MD ne dise si son jet est un succès ou un échec, ou avant que la créature inflige ses dégâts. La créature est immunisée si elle ne peut pas vous entendre ou si les charmes ne l''affectent pas.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:10:08','2016-09-19 08:10:08');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (73,'Secrets magiques additionnels','Vous apprenez deux sorts de votre choix de n''importe quelle classe. Les sorts choisis doivent être d''un niveau que vous pouvez lancer, comme le montre la table du barde, ou être des sorts mineurs. Les sorts choisis comptent comme des sorts de barde pour vous, mais ne comptent pas pour le nombre de sorts de barde connus.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:10:33','2016-09-19 08:10:33');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (74,'Compétences hors pair','Quand vous faites un jet de caractéristique, vous pouvez utiliser une Inspiration bardique. Lancez le dé d''Inspiration bardique et ajoutez le résultat à votre jet de caractéristique. Vous pouvez choisir de le faire après avoir fait votre jet de votre caractéristique, mais vous devez le faire avant que le MD ne vous indique si vous réussissez ou échouez.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:11:16','2016-09-19 08:11:16');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (75,'Maîtrises additionnelles (arme/armure)','Lorsque vous rejoignez le collège de la Valeur, vous gagnez la maîtrise des armures intermédiaires, des boucliers et des armes de guerre.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:13:01','2016-09-19 08:13:01');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (76,'Inspiration de combat','Vous apprenez à inspirer les autres dans la bataille. Une créature qui a un dé d''Inspiration bardique de votre part peut jeter ce dé et ajouter le résultat au jet de dégâts d''une arme qu''elle vient de faire. Ou bien, quand un jet d''attaque est fait contre cette créature, elle peut utiliser sa réaction pour jeter le dé d''Inspiration Bardes et ajouter le résultat à sa CA contre cette attaque, après avoir vu le jet mais avant de savoir si elle touche ou non.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:14:40','2016-09-19 08:14:40');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (77,'Magie de bataille','Vous maîtrisez l''art de tisser les sorts et d''utiliser une arme en un seul acte harmonieux. Lorsque vous utilisez votre action pour lancer un sort de barde, vous pouvez faire une attaque avec une arme en tant qu''action bonus.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:15:20','2016-09-19 08:15:20');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (78,'Renvoi de morts-vivants 1','Au prix d''une action, vous présentez votre symbole sacré en psalmodiant une prière contre les morts-vivants. Chaque mort-vivant qui peut vous voir ou vous entendre et qui se trouve à 30 pieds ou moins de vous doit effectuer un jet de sauvegarde de Sagesse. Si la créature rate son jet de sauvegarde, elle est renvoyée pendant 1 minute ou jusqu''à ce qu''elle prenne des dégâts.\n\nUne créature renvoyée doit passer ses actions à essayer de s''éloigner le plus loin possible de vous et ne peut volontairement se rapprocher à moins de 30 pieds de vous. Elle ne peut pas avoir de réactions et peut utiliser ses actions seulement pour courir ou essayer de s''échapper d''un effet immobilisant. S''il n''y a nulle part où aller, la créature peut utiliser l''action d''esquive.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'SHORT',NULL,2,'2016-09-19 08:19:48','2016-11-21 19:57:40');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (79,'Destruction de morts-vivants CR 1/2','Quand un mort-vivant rate son jet de sauvegarde contre votre capacité de Renvoi de morts-vivants, la créature est immédiatement détruite si son CR est égal ou inférieur à 1/2.\n','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,2,'2016-09-19 08:21:12','2016-11-21 20:00:59');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (80,'Intervention divine','Vous pouvez faire appel à votre divinité pour qu''elle intervienne en cas de besoin réel. Implorer l''aide de votre divinité vous oblige à utiliser votre action. Décrivez l''aide que vous recherchez, et lancez 1d100. Si vous obtenez un nombre égal ou inférieur à votre niveau de clerc, votre divinité intervient. Le MD choisit la nature de l''intervention ; l''effet d''un sort de clerc ou d''un sort de domaine est approprié. Si votre divinité intervient, vous ne pouvez plus utiliser cette capacité durant les 7 prochains jours. Dans le cas contraire, vous pouvez l''utiliser à nouveau après avoir terminé un repos long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'LONG',NULL,2,'2016-09-19 08:21:58','2016-11-21 20:04:44');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (81,'Maîtrise additionnelle (armure lourde)','Vous gagnez la maîtrise des armures lourdes.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:24:44','2016-09-19 08:24:44');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (82,'Disciple de la vie','Vos sorts de soins sont plus efficaces. Chaque fois que vous utilisez un sort de niveau 1 ou supérieur pour redonner des points de vie à une créature, la créature regagne un nombre de points de vie additionnel égal à 2 + le niveau du sort.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:25:24','2016-09-19 08:25:24');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (83,'Canalisation divine : préservation de vie 1','Vous pouvez utiliser votre Canalisation divine pour soigner des blessures graves. Au prix d''une action, vous présentez votre symbole sacré et invoquez une énergie curative qui redonne un montant de points de vie égal à 5 fois votre niveau de clerc. Choisissez une ou plusieurs créatures à 30 pieds ou moins de vous, et divisez ces points entre elles. Cette capacité ne peut pas ramener une créature à plus de 50% de ses points de vie maximums, et elle ne fonctionne pas sur les morts-vivants et les créatures artificielles.  Vous pouvez utiliser ce don 1 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'SHORT',NULL,3,'2016-09-19 08:26:02','2016-11-21 20:17:54');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (84,'Soigneur sacré','Les sorts de guérison que vous lancez sur les autres vous guérissent aussi. Lorsque vous lancez un sort de niveau 1 ou supérieur pour redonner des points de vie à une créature autre que vous-même, vous regagnez un nombre de points de vie égal à 2 + le niveau du sort.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:27:01','2016-09-19 08:27:01');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (85,'Frappe divine (vie 1)','Vous gagnez la possibilité d’imprégner votre arme d''énergie divine. Une fois par tour, lorsque vous touchez une créature avec une attaque armée, vous pouvez infliger 1d8 de dégâts supplémentaires de type radiant à la cible.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,3,'2016-09-19 08:27:35','2016-11-21 20:12:19');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (86,'Soins suprêmes','Quand vous auriez normalement dû lancer un ou plusieurs dés pour redonner des points de vie avec un sort, appliquez directement le maximum de chaque dé. Par exemple, au lieu de restaurer 2d6 points de vie à une créature, vous restaurez 12.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:28:01','2016-09-19 08:28:01');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (87,'Bénédiction de la connaissance','Vous apprenez deux langues de votre choix. Vous obtenez aussi la maîtrise de deux compétences de votre choix parmi les suivantes : Arcanes, Histoire, Nature ou Religion. Votre bonus de maîtrise est doublé pour les jets de caractéristiques utilisant une de ces compétences.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:28:44','2016-09-19 08:28:44');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (88,'Canalisation divine : connaissance des âges 1','Vous pouvez utiliser votre Canalisation divine pour tirer profit d’un puits de connaissance divin. Au prix d’une action, vous choisissez une compétence ou un outil. Pendant 10 minutes, vous obtenez la maîtrise de la compétence ou de l’outil choisi. Vous pouvez utiliser ce don 1 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'SHORT',NULL,3,'2016-09-19 08:29:16','2016-11-21 20:17:30');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (89,'Canalisation divine : lecture des pensées 1','Vous pouvez utiliser votre Canalisation divine pour lire les pensées d’une créature. Vous pouvez ensuite avoir accès à l’esprit de la créature et la commander. Par une action, choisissez une créature que vous pouvez voir située à 18 mètres ou moins de vous. La créature doit effectuer un jet de sauvegarde de Sagesse. Si la créature le réussi, vous ne pouvez réutiliser cette capacité sur la créature qu’après avoir terminé un repos long. Si la créature échoue, vous pouvez lire la surface de ses pensées (celles les plus en vue dans son esprit, représentant ses émotions et ce à quoi elle pense actuellement) lorsqu’elle est à 60 pieds ou moins de vous. Cet effet dure une minute. Pendant cette durée, vous pouvez utiliser votre action pour mettre fin à cet effet et lancer le sort suggestion à la créature sans consommer d’emplacement de sort. La créature échoue automatiquement son jet de sauvegarde contre ce sort.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'LONG',NULL,3,'2016-09-19 08:29:56','2016-11-21 20:31:10');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (90,'Incantation puissante','Vous ajoutez votre modificateur de Sagesse aux dégâts que vous infligez avec tous sorts mineurs de clerc.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:30:19','2016-09-19 08:30:19');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (91,'Visions du passé','Vous pouvez rappeler les visions du passé en relation avec un objet que vous tenez, ou proche de vous. Vous passez au moins une minute à prier et à méditer, puis vous recevez sous forme de rêve de vagues aperçus des événements récents. Vous pouvez méditer de cette manière un nombre de minutes égal votre valeur de Sagesse et devez maintenir votre concentration comme pour lancer un sort. Une fois que vous avez utilisé cette capacité, vous ne pouvez plus l’utiliser jusqu’à ce vous terminiez un repos court ou long.\n\nLecture d’un objet. Si vous tenez un objet lorsque vous méditez, vous pouvez voir des visions de son précédent propriétaire. Après 1 minute de médiation, vous apprenez comment son propriétaire a acquis et perdu l’objet, ainsi que les événements importants les plus récents concernant cet objet et ce propriétaire. Si l’objet a appartenu à une autre créature dans un passé récent (un nombre de jours égal à votre valeur de Sagesse), vous pouvez passer 1 minute supplémentaire pour chaque possesseur de l’objet afin d’apprendre les mêmes informations sur ces créatures.\n\nLecture d’une zone. Pendant que vous méditez, vous observez des visions des environs proches (une salle, une rue, un tunnel, une clairière ou autre, d’une taille maximale d’un cube de 15 mètres d’arrête) jusqu''à un nombre de jours passés égal à votre valeur de Sagesse. Pour chaque minute passée à méditer, vous apprenez des informations sur un événement important, en commençant par le plus récent. Ces événements incluent de fortes émotions telles que des batailles ou des trahisons, mariages ou meurtres, naissances et funérailles. Cependant, ils peuvent aussi inclure des événements sans importance qui sont néanmoins important dans votre situation actuelle.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'SHORT',NULL,2,'2016-09-19 08:31:04','2016-11-21 20:30:57');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (92,'Bénédiction du filou','Vous pouvez utiliser votre action pour toucher une créature consentante (autre que vous-même) et lui donner un avantage aux jets de Dextérité (Discrétion). Cette bénédiction dure pendant 1 heure, ou jusqu''à ce que vous utilisiez cette capacité à nouveau.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:31:34','2016-09-19 08:31:34');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (93,'Canalisation divine : invocation de duplicité 1','Vous pouvez utiliser votre Canalisation divine pour créer une copie illusoire de vous-même. Par une action, vous créez une illusion parfaite de vous-même qui dure pendant 1 minute, ou jusqu''à ce que vous perdiez votre concentration (comme si vous étiez concentré sur un sort). L''illusion apparaît dans un espace inoccupé que vous pouvez voir dans un rayon de 30 pieds autour de vous. Par une action bonus à votre tour, vous pouvez déplacer l''illusion jusqu''à 30 pieds, dans un espace que vous pouvez, et sans l''éloigner de plus de 120 pieds de vous. Durant ce temps, vous pouvez lancer des sorts comme si vous étiez dans l''espace de l''illusion, mais vous devez utiliser vos propres sens. En outre, lorsque vous et votre illusion êtes à 5 pieds mètre ou moins d''une créature qui peut voir l''illusion, vous avez l''avantage aux jets d''attaque contre cette créature, l''illusion distrayant la cible.  Vous pouvez utiliser ce don 1 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'SHORT',NULL,2,'2016-09-19 08:32:57','2016-11-21 20:19:39');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (94,'Canalisation divine : manteau d''ombres 1','Vous pouvez utiliser votre Canalisation divine pour disparaître. Par une action, vous devenez invisible jusqu''à la fin de votre prochain tour. Vous redevenez visible si vous attaquez ou lancez un sort. Vous pouvez utiliser ce don 2 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',NULL,3,'2016-09-19 08:33:26','2016-11-21 20:28:42');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (95,'Frappe divine (ruse)','Vous gagnez la possibilité d’imprégner votre arme de poison, un cadeau de votre divinité. Une fois à chacun de vos tours, lorsque vous touchez une créature avec une attaque d''arme, vous pouvez infliger 1d8 de dégâts de poison supplémentaires. Lorsque vous atteignez le niveau 14, les dégâts supplémentaires passent à 2d8.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 08:36:48','2016-09-19 08:36:48');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (96,'Duplicité améliorée','Vous pouvez créer jusqu''à quatre doublons de vous-même, au lieu d''un, lorsque vous utilisez Invocation de duplicité. Par une action bonus à votre tour, vous pouvez déplacer un certain nombre d''entre eux jusqu''à 30 pieds, et sans les éloigner de plus de 120 pieds de vous.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 09:04:43','2016-09-19 09:04:43');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (97,'Maîtrises additionnelles (armes martiales/armures lourdes)','Vous gagnez la maîtrise des armes de guerres et des armures lourdes.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:40:19','2016-09-19 13:40:19');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (98,'Prêtre de guerre','Votre dieu vous inspire lors des combats. Lorsque vous utilisez l’action Attaquer, vous pouvez faire une attaque avec une arme en tant qu’action bonus. Vous pouvez utiliser cette capacité un nombre de fois égal à votre modificateur de Sagesse (minimum 1). Vous regagnez les attaques utilisées à la fin d’un repos long.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,1,'LONG',NULL,3,'2016-09-19 13:41:24','2016-11-21 20:32:15');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (99,'Canalisation divine : coup au but 1','Vous pouvez utiliser votre Canalisation divine pour frapper avec une grande précision. Lorsque vous faites un jet d’attaque, vous pouvez utilisez votre Canalisation divine pour gagner un bonus de +10 au jet. ','ACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'SHORT',NULL,2,'2016-09-19 13:42:33','2016-11-21 20:33:23');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (100,'Canalisation divine : bénédiction du dieu de la Guerre 1','Lorsqu''une créature dans un rayon de 30 pieds autour de vous fait un jet d’attaque, vous pouvez utiliser votre réaction pour faire bénéficier cette créature d’un bonus de +10 à son jet, en utilisant votre Canalisation divine.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',NULL,2,'2016-09-19 13:43:27','2016-11-21 20:36:10');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (101,'Frappe divine (guerre 1)','Vous gagnez la possibilité d’imprégner votre arme d’énergie divine. Une fois à chacun de vos tours, lorsque vous touchez une créature avec une attaque d''arme, vous pouvez infliger 1d8 de dégâts supplémentaires du même type que celui de votre arme.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,2,'2016-09-19 13:45:15','2016-11-21 20:37:59');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (102,'Avatar de bataille','Vous obtenez la résistance aux dégâts contondants, perforants et tranchants de toute arme non magique.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:45:40','2016-09-19 13:45:40');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (103,'Sort lumière bonus','Vous gagnez le sort mineur lumière si vous ne le connaissez pas déjà.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:46:39','2016-09-19 13:46:39');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (104,'Flamme gardienne','Vous pouvez interposer une lumière divine entre vous et un ennemi qui attaque. Lorsque vous êtes attaqué par une créature située à 30 pieds ou moins de vous et que vous pouvez voir, vous pouvez utiliser votre réaction pour imposer un désavantage à son jet d''attaque, en faisant éclater une lumière avant qu''il touche ou rate. Un attaquant qui ne peut pas être aveuglé est immunisé face à cette capacité. Vous pouvez utiliser cette capacité un nombre de fois égal à votre modificateur de Sagesse (minimum une fois). Vous en retrouver toutes les utilisations dépensées lorsque vous terminez un repos long.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'LONG',NULL,3,'2016-09-19 13:47:08','2016-11-21 20:39:24');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (105,'Canalisation divine : radiance de l''aube 1','Vous pouvez utiliser votre Canalisation divine pour exploiter la lumière du soleil, bannissant les ténèbres et infligeant des dégâts radiants à vos ennemis. Par une action, vous présentez votre symbole sacré et toutes les ténèbres magiques dans un rayon de 30 pieds autour de vous sont dissipées. En outre, chaque créature hostile dans un rayon de 30 pieds autour de vous doit réaliser un jet de sauvegarde de Constitution. Une créature prend 2d10 + votre niveau de clerc de dégâts radiants en cas d''échec, ou la moitié de ces dégâts en cas de réussite. Une créature qui dispose d''une couverture totale vis à vis de vous n''est pas affectée.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'SHORT',NULL,2,'2016-09-19 13:48:01','2016-11-21 21:10:40');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (106,'Flamme gardienne améliorée','Vous pouvez interposer une lumière divine entre vous et un ennemi qui attaque. Lorsque vous êtes attaqué ou une autre personne est attaquée par une créature située à 30 pieds ou moins de vous et que vous pouvez voir, vous pouvez utiliser votre réaction pour imposer un désavantage à son jet d''attaque, en faisant éclater une lumière avant qu''il touche ou rate. Un attaquant qui ne peut pas être aveuglé est immunisé face à cette capacité. Vous pouvez utiliser cette capacité un nombre de fois égal à votre modificateur de Sagesse (minimum une fois). Vous en retrouver toutes les utilisations dépensées lorsque vous terminez un repos long.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'LONG',104,2,'2016-09-19 13:49:44','2016-11-21 20:43:22');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (109,'Couronne de lumière','Vous pouvez utiliser votre action pour activer une aura de lumière du soleil qui dure pendant 1 minute, ou jusqu''à ce que vous la rejetiez à l''aide d''une autre action. Vous émettez une lumière vive dans un rayon de 60 pieds et une lumière faible sur 30 pieds supplémentaires. Vos ennemis dans la zone de lumière vive ont un désavantage aux jets de sauvegarde contre tout sort qui inflige des dégâts de feu ou des dégâts radiants.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:53:12','2016-09-19 13:53:12');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (110,'Acolyte de la Nature','Vous apprenez un sort mineur de druide de votre choix. Vous gagnez également la maîtrise d''une des compétences suivantes de votre choix : Dressage, Nature ou Survie.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:53:47','2016-09-19 13:53:47');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (111,'Canalisation divine : charme des animaux et des plantes','Vous pouvez utiliser votre Canalisation divine pour charmer des animaux et de plantes. Par une action, vous présentez votre symbole sacré et invoquez le nom de votre divinité. Chaque créature du type bête ou plante qui peut vous voir et qui se situe dans un rayon de 30 pieds autour de vous doit réaliser un jet de sauvegarde de Sagesse. Si la créature rate son jet de sauvegarde, vous la charmez pendant 1 minute, ou jusqu''à ce qu''elle subisse des dégâts. Tant que vous la charmez, elle est amicale envers vous et toutes autres créatures que vous désignez.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:54:56','2016-09-19 13:54:56');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (112,'Affaiblissement des éléments','Lorsque vous ou une créature située à 30 pieds ou moins de vous prend des dégâts d''acide, de froid, de feu, de foudre ou de tonnerre, vous pouvez utiliser votre réaction pour accorder à la créature une résistance à ce type de dégâts.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:55:28','2016-09-19 13:55:28');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (113,'Frappe divine (nature)','Vous gagnez la possibilité d’imprégner votre arme d’énergie divine. Une fois à chacun de vos tours, lorsque vous touchez une créature avec une attaque d''arme, vous pouvez infliger 1d8 de dégâts supplémentaires de froid, de feu ou de foudre (selon votre choix) à la cible. Lorsque vous atteignez le niveau 14, les dégâts supplémentaires passent à 2d8.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:56:16','2016-09-19 13:56:16');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (114,'Maître de la Nature','Vous gagnez la possibilité de commander les animaux et les créatures végétales. Lorsque des créatures sont charmées par votre capacité de Charme des animaux et des plantes, vous pouvez utiliser une action bonus à votre tour pour commander verbalement ces créatures et leur indiquer ce qu''elles doivent faire durant leur prochain tour.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:56:58','2016-09-19 13:56:58');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (115,'Colère de la Tempête','Vous pouvez réprimander vos attaquants avec la puissance du tonnerre. Lorsqu’une créature que vous pouvez voir dans un rayon de 5 pieds autour de vous vous touche, vous pouvez utiliser votre réaction pour obliger l’attaquant à effectuer un jet de sauvegarde de Dextérité. En cas d’échec, la créature subit 2d8 points de dégâts de foudre ou de tonnerre (selon votre choix), ou la moitié en cas de réussite. Vous pouvez utiliser cette capacité un nombre de fois égal à votre modificateur de Sagesse (minimum 1). Vous regagnez toutes vos charges après un repos long.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:58:07','2016-09-19 13:58:07');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (116,'Canalisation divine : colère destructrice','Vous pouvez utiliser votre Canalisation divine pour manier la puissance de la tempête avec une férocité incontrôlée. Lorsque vous infligez des dégâts de foudre ou de tonnerre, vous pouvez utiliser votre Canalisation divine pour effectuer des dégâts maximum au lieu de lancer des dés.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:58:39','2016-09-19 13:58:39');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (117,'Frappe de l''éclair','Lorsque vous infligez des dégâts de foudre à une créature de taille G ou inférieure, vous pouvez également la repousser à 10 pieds de vous.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:59:12','2016-09-19 13:59:12');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (118,'Frappe divine (tempête)','Vous gagnez la possibilité d’imprégner votre arme d’énergie divine. Une fois à chacun de vos tours, lorsque vous touchez une créature avec une attaque d''arme, vous pouvez infliger 1d8 de dégâts de tonnerre supplémentaires. Lorsque vous atteignez le niveau 14, les dégâts supplémentaires passent à 2d8.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 13:59:59','2016-09-19 13:59:59');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (119,'Fils de la tempête','Vous obtenez une vitesse de vol égale à votre vitesse de déplacement tant que vous n’êtes ni sous terre, ni dans un intérieur.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:00:34','2016-09-19 14:00:34');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (120,'Forme animale 1','Vous pouvez utiliser votre action pour prendre par magie la forme d''une bête que vous avez déjà vue auparavant. Vous pouvez utiliser ce pouvoir deux fois. Vous récupérez les utilisations dépensées après avoir effectué un repos court ou long.\n\nVous pouvez vous transformer en n''importe qu''elle bête ayant un facteur de puissance de 1/4 ou inférieur et qui n''a ni vitesse de vol ni vitesse de nage. Exemple : loup\n\nVous pouvez rester sous forme animale un nombre d''heures égal à la moitié de votre niveau de druide (arrondi à l''entier inférieur). Une fois le temps écoulé, vous reprenez votre forme normale à moins que vous ne dépensiez une nouvelle utilisation de cette capacité. Vous pouvez également revenir plus tôt à votre forme normale en utilisant votre action lors de votre tour de jeu. Vous revenez automatiquement à votre forme normale si vous tombez inconscient, si vous tombez à 0 point de vie ou si vous mourrez. Tant que vous êtes transformé, les règles suivantes s''appliquent :\n\nVos statistiques de jeu sont remplacées par celles de la bête, mais vous conservez votre alignement, votre personnalité et vos valeurs de Charisme, Intelligence et Sagesse. Vous conservez également vos maîtrises de jets de sauvegarde et de compétences, en plus de gagner celles de la créature. Si la créature a les mêmes maîtrises que vous et que son bonus dans ce bloc de statistique est supérieur au vôtre, utilisez son bonus. Si la créature possède des actions légendaires ou des actions de repaire, vous ne pouvez pas les utiliser.\n\nQuand vous vous transformez, vous endossez également les points de vie de la bête et ses dés de vie. Lorsque vous reprenez votre forme vous récupérez le nombre de points de vie que vous aviez avant votre transformation. De plus, si vous vous retransformez parce que vous êtes tombé à 0 point de vie, tous les dégâts supplémentaires sont encaissés par les points de vie de votre forme normale. Par exemple, si vous subissez 10 points de dégâts sous forme animale alors que vous n''aviez plus qu''un point de vie, vous retournez à votre forme normale et encaissez 9 points de dégâts. Tant que ces dégâts résiduels ne font pas tomber les points de vie de votre forme normale à 0, vous ne sombrez pas dans l''inconscience.\n\nVous ne pouvez pas lancer de sorts, et votre capacité à parler ou effectuer toute action qui nécessite des mains est limitée aux possibilités qu''offre votre forme animale. Toutefois, vous transformer ne brise pas votre concentration sur un sort que vous aviez déjà lancé, ni ne vous empêche d''utiliser une action pour rediriger un sort, comme le sort appel de la foudre, que vous auriez déjà lancé.\n\nVous conservez les bénéfices de toutes vos capacités de classe, de race, ou de n''importe quelle autre origine, et vous pouvez les utiliser si vous en êtes physiquement capable. Cependant, vous ne pouvez pas utiliser de sens spéciaux, comme la vision dans le noir, sauf si votre forme animale les possède également.\n\nVous choisissez si votre équipement tombe sur le sol, fusionne avec votre nouvelle forme ou est porté par votre nouvelle forme. L''équipement porté fonctionne correctement, mais le MD décide pour chaque pièce d''équipement s''il est possible pour votre nouvelle forme de la porter, en fonction de la morphologie et de la taille de la bête. Votre équipement ne change pas de taille ou de forme pour s''adapter à votre nouvelle forme, et tout l''équipement que la nouvelle forme ne peut pas porter tombe sur le sol ou fusionne avec vous. L''équipement qui fusionne avec la nouvelle forme n''a aucun effet tant que vous restez sous cette forme.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',NULL,4,'2016-09-19 14:31:28','2016-12-04 11:14:46');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (121,'Éternelle jeunesse','La magie primordiale qui vous parcourt ralentit le vieillissement de votre corps. Vous ne prenez qu''un an d''âge toutes les dix années écoulées.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:31:52','2016-09-19 14:31:52');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (122,'Incantation animale','Vous pouvez lancer sous forme animale la plupart des sorts que vous connaissez. Vous pouvez réaliser les composantes somatiques et verbales des sorts de druide lorsque vous êtes sous forme animale, mais vous ne pouvez pas lancer de sorts nécessitant une composante matérielle.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:32:18','2016-09-19 14:32:18');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (123,'Archidruide','Vous pouvez utiliser votre forme animale un nombre illimité de fois par jour. De plus, vous pouvez ignorer les composantes verbales et somatiques de vos sorts de même que toute composante matérielle non coûteuse qui n''est consommée par le sort. Vous gagnez ces avantages que vous soyez sous forme normale ou animale.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:33:01','2016-09-19 14:33:01');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (124,'Sort druidique supplémentaire','Vous apprenez un sort mineur de druide supplémentaire de votre choix.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:33:32','2016-09-19 14:33:32');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (125,'Récupération naturelle','Vous pouvez récupérer une partie de votre énergie magique en méditant et communiant avec la nature. Lors d''un repos court, vous choisissez les emplacements de sort que vous souhaitez récupérer. Les emplacements de sort doivent avoir un niveau cumulé inférieur ou égal à la moitié de votre niveau de druide (arrondi à l''entier supérieur), et aucun de ces emplacements ne doit être de niveau 6 ou supérieur. Vous ne pouvez pas utiliser de nouveau cette capacité avant d''avoir terminé un repos long. Par exemple, si vous êtes un druide de niveau 4, vous pouvez récupérer jusqu''à deux niveaux d''emplacement de sort. Vous pouvez soit choisir un emplacement de sort de niveau 2, soit deux emplacements de sort de niveau 1.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:34:15','2016-09-19 14:34:15');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (126,'Sorts du cercle','Votre connexion mystique à la terre vous permet de lancer certains sorts. Aux niveaux 3, 5, 7 et 9, vous accédez à des sorts de cercle liés à la terre qui vous a vu devenir druide. Choisissez ce terrain (arctique, désert, forêt, littoral, marais, montagne, plaine ou Outreterre) et consultez la liste de sorts associée. Une fois que vous gagnez accès à un sort de cercle, ce sort est constamment préparé et ne compte pas dans le total de sorts que vous pouvez préparer chaque jour. Si vous accédez à un sort qui n''apparaît pas dans la liste de sorts du druide, ce sort est néanmoins considéré comme étant un sort de druide pour vous.\n\nArctique\n3    immobilisation de personne, croissance d''épines\n5 lenteur, giboulée\n7    liberté de mouvement, tempête de grêle\n9   communion avec la nature, cône de froid\n\nDésert\n3    flou, silence\n5    création de nourriture et d''eau, protection contre une énergie\n7  flétrissement, terrain hallucinatoire\n9    nuée d''insectes, mur de pierre\n\nForêt\n3 pattes d''araignée, peau d''écorce\n5   croissance végétale, appel de la foudre\n7  divination, liberté de mouvement\n9 communion avec la nature, voyage par les arbres\n\nLittoral\n3  image miroir, foulée brumeuse\n5    marche sur l''onde, respiration aquatique\n7    liberté de mouvement, contrôle de l''eau\n9 invocation d''élémentaire, scrutation\n\nMarais\n3  ténèbres, flèche acide de Melf\n5   marche sur l''onde, nuage puant\n7  liberté de mouvement, localisation de créature\n9   nuée d''insectes, scrutation\n\nMontagne\n3 pattes d''araignée, croissance d''épines\n5 éclair, fusion dans la pierre\n7    façonnage de la pierre, peau de pierre\n9   passe-muraille, mur de pierre\n\nPlaine\n3  invisibilité, passage sans trace\n5 lumière du jour, rapidité\n7    divination, liberté de mouvement\n9 songe, nuée d''insectes\n\nOutreterre\n3    pattes d''araignée, toile d''araignée\n5    état gazeux, nuage puant\n7 invisibilité suprême, façonnage de la pierre\n9 nuée d''insectes, brume mortelle','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:39:26','2016-09-19 14:39:26');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (127,'Traversée des terrains','Vous déplacer sur un terrain difficile non magique ne vous coûte pas de déplacement supplémentaire. Vous pouvez également traverser la végétation non magique sans être ralenti et sans subir de dégâts si elle est constituée d''épines, de pointes ou d''autres inconvénients similaires. De plus, vous avez l''avantage aux jets de sauvegarde contre les plantes qui ont été créées magiquement ou manipulées pour empêcher les mouvements, comme celles créées par le sort enchevêtrement.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:39:57','2016-09-19 14:39:57');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (128,'Protection de la nature','Vous ne pouvez plus être charmé ou effrayé par les élémentaires ou les fées, et vous êtes immunisé contre les poisons et les maladies.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:40:22','2016-09-19 14:40:22');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (129,'Sanctuaire de la nature','Les créatures du monde naturel ressentent votre connexion avec la nature et hésitent à vous attaquer. Quand une bête ou une plante vous attaque, cette créature doit réussir un jet de sauvegarde de Sagesse contre votre DD de sort de druide. Si elle échoue, la créature doit choisir une autre cible, ou bien l''attaque rate automatiquement. En cas de jet de sauvegarde réussi, la créature est immunisée à cet effet pour 24 heures. La créature est consciente de cet effet avant qu''elle n''effectue son attaque contre vous.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:40:56','2016-09-19 14:40:56');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (130,'Combat sous forme animale','Vous obtenez la possibilité d''utiliser la forme animale durant votre tour par une action bonus, au lieu d''une action. De plus, lorsque vous êtes transformé via forme animale, vous pouvez utiliser une action bonus pour dépenser un emplacement de sort et récupérer ainsi 1d8 points de vie par niveau d''emplacement de sort dépensé.','ACTION_BONUS',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:46:12','2016-09-19 14:46:12');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (131,'Formes de cercle','Les rites de votre cercle vous permettent de prendre la forme d''animaux plus dangereux. Vous pouvez utiliser la forme animale pour vous transformer en une bête de facteur de puissance inférieur ou égal à 1 (ignorez la colonne FP maximum de la table Types de bêtes, vous êtes cependant toujours soumis aux autres restrictions).\n\nÀ partir du niveau 6, vous pouvez vous transformer en une bête de facteur de puissance inférieur ou égal à votre niveau de druide divisé par 3, arrondi à l''entier inférieur.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:47:05','2016-09-19 14:47:05');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (132,'Attaque bestiale','Vos attaques sous forme animale comptent comme des attaques magiques pour ce qui est de surmonter la résistance et l''immunité aux attaques et dégâts non magiques.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:47:26','2016-09-19 14:47:26');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (133,'Forme élémentaire','Vous pouvez dépenser deux utilisations de forme animale en même temps pour vous transformer en un élémentaire d''air, d''eau, de terre ou de feu.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:47:57','2016-09-19 14:47:57');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (134,'Milles formes','Vous avez appris à utiliser la magie pour améliorer votre forme physique de façon plus subtile. Vous pouvez lancer le sort modification d''apparence à volonté.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-09-19 14:48:31','2016-09-19 14:48:31');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (135,'Critique brutal 2','Vous pouvez lancer 2 dés de dégâts de votre arme en plus lorsque vous déterminez les dégâts supplémentaires que vous infligez sur un coup critique réussi avec une attaque de corps à corps.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,49,2,'2016-10-19 09:22:21','2016-12-04 11:27:11');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (136,'Critique brutal 3','Vous pouvez lancer 3 dés de dégâts de votre arme en plus lorsque vous déterminez les dégâts supplémentaires que vous infligez sur un coup critique réussi avec une attaque de corps à corps.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,135,2,'2016-10-19 09:22:45','2016-12-04 11:27:21');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (137,'Forme animale 2','Vous pouvez utiliser votre action pour prendre par magie la forme d''une bête que vous avez déjà vue auparavant. Vous pouvez utiliser ce pouvoir deux fois. Vous récupérez les utilisations dépensées après avoir effectué un repos court ou long.\n\nVous pouvez vous transformer en n''importe qu''elle bête ayant un facteur de puissance de 1/2 ou inférieur et qui n''a pas de vitesse de vol. Exemple : crocodile.\n\nVous pouvez rester sous forme animale un nombre d''heures égal à la moitié de votre niveau de druide (arrondi à l''entier inférieur). Une fois le temps écoulé, vous reprenez votre forme normale à moins que vous ne dépensiez une nouvelle utilisation de cette capacité. Vous pouvez également revenir plus tôt à votre forme normale en utilisant votre action lors de votre tour de jeu. Vous revenez automatiquement à votre forme normale si vous tombez inconscient, si vous tombez à 0 point de vie ou si vous mourrez. Tant que vous êtes transformé, les règles suivantes s''appliquent :\n\nVos statistiques de jeu sont remplacées par celles de la bête, mais vous conservez votre alignement, votre personnalité et vos valeurs de Charisme, Intelligence et Sagesse. Vous conservez également vos maîtrises de jets de sauvegarde et de compétences, en plus de gagner celles de la créature. Si la créature a les mêmes maîtrises que vous et que son bonus dans ce bloc de statistique est supérieur au vôtre, utilisez son bonus. Si la créature possède des actions légendaires ou des actions de repaire, vous ne pouvez pas les utiliser.\n\nQuand vous vous transformez, vous endossez également les points de vie de la bête et ses dés de vie. Lorsque vous reprenez votre forme vous récupérez le nombre de points de vie que vous aviez avant votre transformation. De plus, si vous vous retransformez parce que vous êtes tombé à 0 point de vie, tous les dégâts supplémentaires sont encaissés par les points de vie de votre forme normale. Par exemple, si vous subissez 10 points de dégâts sous forme animale alors que vous n''aviez plus qu''un point de vie, vous retournez à votre forme normale et encaissez 9 points de dégâts. Tant que ces dégâts résiduels ne font pas tomber les points de vie de votre forme normale à 0, vous ne sombrez pas dans l''inconscience.\n\nVous ne pouvez pas lancer de sorts, et votre capacité à parler ou effectuer toute action qui nécessite des mains est limitée aux possibilités qu''offre votre forme animale. Toutefois, vous transformer ne brise pas votre concentration sur un sort que vous aviez déjà lancé, ni ne vous empêche d''utiliser une action pour rediriger un sort, comme le sort appel de la foudre, que vous auriez déjà lancé.\n\nVous conservez les bénéfices de toutes vos capacités de classe, de race, ou de n''importe quelle autre origine, et vous pouvez les utiliser si vous en êtes physiquement capable. Cependant, vous ne pouvez pas utiliser de sens spéciaux, comme la vision dans le noir, sauf si votre forme animale les possède également.\n\nVous choisissez si votre équipement tombe sur le sol, fusionne avec votre nouvelle forme ou est porté par votre nouvelle forme. L''équipement porté fonctionne correctement, mais le MD décide pour chaque pièce d''équipement s''il est possible pour votre nouvelle forme de la porter, en fonction de la morphologie et de la taille de la bête. Votre équipement ne change pas de taille ou de forme pour s''adapter à votre nouvelle forme, et tout l''équipement que la nouvelle forme ne peut pas porter tombe sur le sol ou fusionne avec vous. L''équipement qui fusionne avec la nouvelle forme n''a aucun effet tant que vous restez sous cette forme.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',120,3,'2016-11-11 14:26:12','2016-12-04 11:14:35');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (138,'Forme animale 3','Vous pouvez utiliser votre action pour prendre par magie la forme d''une bête que vous avez déjà vue auparavant. Vous pouvez utiliser ce pouvoir deux fois. Vous récupérez les utilisations dépensées après avoir effectué un repos court ou long.\n\nVous pouvez vous transformer en n''importe qu''elle bête ayant un facteur de puissance de 1 ou inférieur. Il n''y a pas de restriction de mouvement. Exemple : aigle géant.\n\nVous pouvez rester sous forme animale un nombre d''heures égal à la moitié de votre niveau de druide (arrondi à l''entier inférieur). Une fois le temps écoulé, vous reprenez votre forme normale à moins que vous ne dépensiez une nouvelle utilisation de cette capacité. Vous pouvez également revenir plus tôt à votre forme normale en utilisant votre action lors de votre tour de jeu. Vous revenez automatiquement à votre forme normale si vous tombez inconscient, si vous tombez à 0 point de vie ou si vous mourrez. Tant que vous êtes transformé, les règles suivantes s''appliquent :\n\nVos statistiques de jeu sont remplacées par celles de la bête, mais vous conservez votre alignement, votre personnalité et vos valeurs de Charisme, Intelligence et Sagesse. Vous conservez également vos maîtrises de jets de sauvegarde et de compétences, en plus de gagner celles de la créature. Si la créature a les mêmes maîtrises que vous et que son bonus dans ce bloc de statistique est supérieur au vôtre, utilisez son bonus. Si la créature possède des actions légendaires ou des actions de repaire, vous ne pouvez pas les utiliser.\n\nQuand vous vous transformez, vous endossez également les points de vie de la bête et ses dés de vie. Lorsque vous reprenez votre forme vous récupérez le nombre de points de vie que vous aviez avant votre transformation. De plus, si vous vous retransformez parce que vous êtes tombé à 0 point de vie, tous les dégâts supplémentaires sont encaissés par les points de vie de votre forme normale. Par exemple, si vous subissez 10 points de dégâts sous forme animale alors que vous n''aviez plus qu''un point de vie, vous retournez à votre forme normale et encaissez 9 points de dégâts. Tant que ces dégâts résiduels ne font pas tomber les points de vie de votre forme normale à 0, vous ne sombrez pas dans l''inconscience.\n\nVous ne pouvez pas lancer de sorts, et votre capacité à parler ou effectuer toute action qui nécessite des mains est limitée aux possibilités qu''offre votre forme animale. Toutefois, vous transformer ne brise pas votre concentration sur un sort que vous aviez déjà lancé, ni ne vous empêche d''utiliser une action pour rediriger un sort, comme le sort appel de la foudre, que vous auriez déjà lancé.\n\nVous conservez les bénéfices de toutes vos capacités de classe, de race, ou de n''importe quelle autre origine, et vous pouvez les utiliser si vous en êtes physiquement capable. Cependant, vous ne pouvez pas utiliser de sens spéciaux, comme la vision dans le noir, sauf si votre forme animale les possède également.\n\nVous choisissez si votre équipement tombe sur le sol, fusionne avec votre nouvelle forme ou est porté par votre nouvelle forme. L''équipement porté fonctionne correctement, mais le MD décide pour chaque pièce d''équipement s''il est possible pour votre nouvelle forme de la porter, en fonction de la morphologie et de la taille de la bête. Votre équipement ne change pas de taille ou de forme pour s''adapter à votre nouvelle forme, et tout l''équipement que la nouvelle forme ne peut pas porter tombe sur le sol ou fusionne avec vous. L''équipement qui fusionne avec la nouvelle forme n''a aucun effet tant que vous restez sous cette forme.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',137,2,'2016-11-11 14:27:35','2016-12-04 11:15:02');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (139,'Expertise 1','Choisissez deux des compétences que vous maîtrisez. Votre bonus de maîtrise est doublé pour tout jet de caractéristique que vous réalisez et qui utilise l''une des compétences choisies.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:30:54','2016-11-11 14:30:54');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (140,'Expertise 2','Vous pouvez choisir deux maîtrises supplémentaires (compétences ou outils de voleur) et votre bonus de maîtrise est doublé pour tout jet de caractéristique que vous réalisez et qui utilise l''une des compétences choisies.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:32:25','2016-11-11 14:32:25');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (141,'Attaque sournoise','Vous savez comment trouver et exploiter subtilement la distraction d''un ennemi. Une fois par tour, vous pouvez infliger 1d6 points de dégâts supplémentaires à une créature que vous réussissez à toucher si vous avez l''avantage au jet d''attaque. L''attaque doit utiliser une arme de finesse ou une arme à distance. Vous n''avez pas besoin d''avoir l''avantage au jet d''attaque si un autre ennemi de la cible est à 5 pieds ou moins de celle-ci, que cet ennemi n''est pas incapable d''agir, et que vous n''avez pas un désavantage au jet d''attaque. Le nombre de dégâts supplémentaires augmente à mesure que vous gagnez des niveaux dans cette classe','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:33:36','2016-11-11 14:33:36');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (142,'Jargon de voleurs','Au cours de votre formation de roublard, vous avez appris le jargon des voleurs, un mélange secret de dialecte, de jargon et de codes, qui vous permet de cacher des messages dans une conversation apparemment normale. Seule une autre créature qui connaît le jargon des voleurs comprend ces messages. Il faut quatre fois plus de temps pour transmettre un tel message qu''il ne le faudrait pour transmettre la même idée clairement.\n\nEn outre, vous comprenez un ensemble de signes et de symboles secrets utilisés pour transmettre des messages courts et simples, comme pour indiquer qu''une zone est dangereuse ou qu''elle fait partie du territoire d''une guilde des voleurs, qu''un butin est à proximité, ou que les gens d''une région sont des proies faciles ou qu''ils fourniront une maison sûre pour des voleurs en fuite.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:34:01','2016-11-11 14:34:01');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (143,'Action fourbe','Votre vivacité d''esprit et votre agilité vous permettent de vous déplacer et d''agir rapidement. Vous pouvez effectuer une action bonus à chacun de vos tours en combat. Cette action ne peut être utilisée que pour vous désengager, vous cacher ou courir.','ACTION_BONUS',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:34:40','2016-11-11 14:34:40');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (144,'Esquive','Quand un attaquant que vous pouvez voir vous touche avec une attaque, vous pouvez utiliser votre réaction pour réduire de moitié les dégâts de l''attaque contre vous.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:35:25','2016-11-11 14:35:25');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (145,'Évasion','Vous pouvez esquiver certains effets de zone, tels le souffle ardent d''un dragon rouge ou un sort de tempête de glace. Lorsque vous êtes sujet à un effet qui vous permet de faire un jet de sauvegarde de Dextérité pour ne prendre que la moitié des dégâts, vous ne prenez aucun dégât si vous réussissez le jet de sauvegarde, et seulement la moitié des dégâts si vous échouez.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:35:57','2016-11-11 14:35:57');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (146,'Talent','Les compétences que vous avez choisies sont si affinées qu''elles approchent la perfection. Chaque fois que vous faites un jet de caractéristique qui vous permet d''ajouter votre bonus de maîtrise, vous pouvez traiter un résultat au dé de 9 ou moins comme un 10.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:36:31','2016-11-11 14:36:31');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (147,'Sens de l''aveugle','Si vous êtes capable d''entendre, vous pouvez assumer l''emplacement de n''importe quelle créature cachée ou invisible à 10 pieds ou moins de vous.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:37:33','2016-11-11 14:37:33');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (148,'Esprit insaisissable','Vous avez acquis une plus grande force mentale. Vous gagnez la maîtrise aux jets de sauvegarde de Sagesse.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:38:06','2016-11-11 14:38:06');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (149,'Coup de chance','Vous avez un talent surnaturel pour réussir quand vous en avez besoin. Si votre attaque échoue contre une cible à portée, vous pouvez transformer l''échec en succès. Ou bien si vous échouez à un jet de caractéristique, vous pouvez traiter le résultat du d20 comme un 20. Une fois cette capacité utilisée, vous devez terminer un repos court ou long pour pouvoir l''utiliser de nouveau.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'SHORT',NULL,2,'2016-11-11 14:38:59','2016-12-04 11:19:42');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (150,'Mains rapides','Vous pouvez utiliser l''action bonus accordée par votre Action fourbe pour réaliser un jet de Dextérité (Escamotage), utiliser vos outils de voleur pour désarmer un piège ou ouvrir une serrure, ou prendre l''action d''Utiliser un objet.','ACTION_BONUS',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:39:42','2016-11-11 14:39:42');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (151,'Cambriolage','Vous gagnez la possibilité d''escalader plus vite que la normale ; escalader ne vous coûte aucun mouvement supplémentaire. En outre, lorsque vous effectuez un saut en longueur, la distance que vous pouvez couvrir augmente d''une distance égale à votre modificateur de Dextérité x 1 pied.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:40:30','2016-11-11 14:40:30');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (152,'Discrétion suprême','Vous avez l''avantage à tous vos jets de Dextérité (Discrétion) si vous ne vous déplacez pas de plus de la moitié de votre vitesse durant le même tour.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:41:09','2016-11-11 14:41:09');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (153,'Utiliser des objets magiques','Vous en savez assez sur le fonctionnement de la magie pour pouvoir utiliser des objets même s''ils ne vous sont pas destinés. Ignorez toutes les exigences de classe, de race et de niveau pour l''utilisation d''objets magiques.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:41:49','2016-11-11 14:41:49');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (154,'Réflexes de voleur','Vous pouvez jouer deux tours lors du premier round d''un combat. Vous prenez votre premier tour suivant votre initiative normale et votre deuxième tour à votre initiative moins 10. Vous ne pouvez pas utiliser cette capacité si vous êtes surpris.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:42:34','2016-11-11 14:42:34');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (155,'Maîtrises additionnelles (assassin)','Vous gagner la maîtrise du kit de déguisement et du kit d''empoisonneur.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,2,'2016-11-11 14:43:30','2016-11-11 14:43:43');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (156,'Assassin ','Vous êtes au summum de votre art lorsque vous prenez votre adversaire au dépourvu. Vous avez l''avantage à vos jets d''attaque contre toute créature qui n''a pas encore joué son tour durant le combat. En outre, si vous attaquez et touchez une créature surprise, cette attaque est considérée comme un coup critique.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,2,'2016-11-11 14:44:25','2016-11-11 14:45:49');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (157,'Expert en infiltration','Vous pouvez créer de fausses identités pour vous-même. Vous devez passer une semaine et dépenser 25 po pour établir l''histoire, la profession et les affiliations d''une identité. Cependant, vous ne pouvez pas établir une identité qui appartient déjà à une personne existante. Par exemple, vous devriez vous procurer les vêtements adéquats, des lettres d''introduction et une certification paraissant officielle pour vous faire passer pour le membre d''une guilde d''une ville lointaine et ainsi vous infiltrer dans un cercle de marchands. Par la suite, si vous adoptez une nouvelle identité avec un déguisement, les autres créatures croiront que vous êtes cette personne jusqu''à ce qu''elles aient une bonne raison pour penser le contraire.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:45:49','2016-11-11 14:45:49');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (158,'Imposteur','Vous gagnez la capacité d''imiter le discours, l''écriture et le comportement d''une autre personne avec une précision extraordinaire. Vous devez étudier pendant au moins trois heures ces trois aspects (écouter parler la personne, examiner son écriture ou observer ses manières). Votre ruse est indiscernable pour un observateur occasionnel. Si une créature suspecte que quelque chose ne va pas, vous avez l''avantage pour tout jet de Charisme (Tromperie) que vous réaliser pour éviter d''être détecté.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:46:39','2016-11-11 14:46:39');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (159,'Coup mortel','Vous êtes un maître de la mort instantanée. Quand vous attaquez et touchez une créature qui est surprise, celle-ci doit réussir un jet de sauvegarde de Constitution DD 8 + votre modificateur de Dextérité + votre bonus de maîtrise. En cas d''échec, doublez les dégâts de l''attaque contre cette créature.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:47:22','2016-11-11 14:47:22');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (160,'Manipulation à distance','Lorsque vous lancez manipulation à distance, vous pouvez rendre la main spectrale invisible, et vous pouvez effectuer les tâches supplémentaires suivantes avec celle-ci :\n\nVous pouvez ranger un objet que la main tient dans un contenant, porté ou transporté par une autre créature.\nVous pouvez récupérer un objet dans un contenant, porté ou transporté par une autre créature.\nVous pouvez utiliser des outils de voleur pour crocheter les serrures et pour désarmer les pièges à distance.\nVous pouvez effectuer une de ces tâches sans vous faire remarquer par une créature si vous réussissez un jet de Dextérité (Escamotage) opposé à un jet de Sagesse (Perception) de la créature. En outre, vous pouvez utiliser l''action bonus accordé par votre Action fourbe pour contrôler la main.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:54:26','2016-11-11 14:54:26');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (161,'Embuscade magique','Si vous êtes caché d''une créature quand vous lui lancez un sort, la créature a un désavantage à tous les jets de sauvegarde contre ce sort pendant ce tour.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:54:56','2016-11-11 14:54:56');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (162,'Filou versatile','Vous obtenez la possibilité de distraire les cibles avec la main de manipulation à distance. Par une action bonus à votre tour, vous pouvez désigner une créature dans un rayon de 5 pieds autour de la main spectrale créé par le sort. Le faire vous donne l''avantage aux jets d’attaque contre cette créature jusqu''à la fin du tour.','ACTION_BONUS',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:55:46','2016-11-11 14:55:46');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (163,'Voleur de sort','Vous gagnez la capacité de voler magiquement à un lanceur de sorts sa connaissance pour lancer un sort. Immédiatement après qu’une créature jette un sort qui vous cible ou vous inclut dans sa zone d''effet, vous pouvez utiliser votre réaction pour forcer la créature à faire un jet de sauvegarde avec son modificateur de la caractéristique pour lancer des sorts. Le DD est égal à votre sort DD de sauvegarde contre les sorts. En cas d''échec, vous niez l''effet du sort contre vous, et vous volez la connaissance du sort s’il est au moins de niveau 1 et d''un niveau que vous pouvez lancer (le sort ne doit pas forcément être un sort de magicien). Pour les prochaines 8 heures, vous connaissez le sort et pouvez le lancer à l''aide de vos emplacements de sorts. La créature ne peut plus lancer ce sort jusqu''à ce que les 8 heures soient passées. Une fois que vous utilisez cette capacité, vous ne pouvez pas l''utiliser de nouveau jusqu''à ce que vous finissiez un repos long.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'LONG',NULL,2,'2016-11-11 14:56:41','2016-12-04 11:20:49');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (164,'Maître de l''intrigue','Vous gagnez la maîtrise du kit de déguisement, du kit de contrefaçon et d''un type de jeu de votre choix. Vous apprenez également deux langues de votre choix. En outre, vous pouvez infailliblement imiter la tonalité de voix et l''accent d''une créature que vous entendez parler pendant au moins 1 minute, vous permettant de vous faire passer pour un locuteur natif d''une terre particulière, à condition de connaitre la langue.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:57:22','2016-11-11 14:57:22');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (165,'Maître des tactiques','Vous pouvez utiliser l''action Aider en tant qu''action bonus. En outre, lorsque vous utilisez l''action Aider pour aider un allié à attaquer une créature, la cible de cette attaque peut être dans un rayon de 30 pieds autour de vous, au lieu de 5 pieds, si la cible peut vous voir ou vous entendre.','ACTION_BONUS',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:58:16','2016-11-11 14:58:16');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (166,'Manipulateur perspicace','Si vous passez au moins 1 minute à observer ou interagir avec une autre créature en dehors d''une situation de combat, vous pouvez apprendre certaines informations sur ses capacités par rapport aux vôtres. Le MD vous indique si la créature est votre égal, supérieur ou inférieur par rapport à deux des caractéristiques suivantes de votre choix :\n\nValeur d''Intelligence\nValeur de Sagesse\nValeur de Charisme\nNiveau de classe (le cas échéant)\nÀ la discrétion du MD, vous pouvez aussi réaliser que vous connaissez une part de l''histoire de la créature ou l''un de ses traits de personnalité, si elle en possède.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 14:58:57','2016-11-11 14:58:57');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (167,'Détournement','Vous pouvez parfois faire qu''une créature finisse par être la cible d''une attaque qui vous visait. Lorsque vous êtes ciblé par une attaque alors qu''une créature dans un rayon de 5 pieds autour de vous vous offre un abri contre cette attaque, vous pouvez utiliser votre réaction pour que la cible de l''attaque soit cette créature, à votre place.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 15:00:00','2016-11-11 15:00:00');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (168,'Âme de tromperie','Vos pensées ne peuvent être lues par télépathie ou par d''autres moyens, sauf si vous le permettez. Vous pouvez présenter de fausses pensées en faisant un jet de Charisme (Tromperie) opposé à un jet de Sagesse (Intuition) du lecteur de votre esprit. En outre, peu importe ce que vous dites, la magie qui permettrait de déterminer si vous dites la vérité indique que vous ne mentez pas, si vous le souhaitez, et vous ne pouvez pas être obligé de dire la vérité par magie.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 15:00:58','2016-11-11 15:00:58');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (169,'Insaisissable','Les attaquants gagnent rarement le dessus sur vous. Aucun jet d''attaque n''a l''avantage contre vous tant que vous n''êtes pas incapable d''agir.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-11 20:40:22','2016-11-11 20:40:22');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (170,'Inspiration bardique d6','Vous pouvez inspirer les autres en maniant les mots ou la musique. Pour ce faire, utilisez une action bonus à votre tour pour choisir une créature autre que vous-même dans un rayon de 18 mètres autour de vous et qui peut vous entendre. Cette créature gagne un dé d''Inspiration bardique (d6). Une fois dans les 10 minutes suivantes, la créature peut lancer le dé et ajouter le nombre obtenu à un jet de caractéristique, d''attaque ou de sauvegarde qu''elle vient de faire. La créature peut attendre de voir le résultat de jet de caractéristique, d''attaque ou de sauvegarde avant de décider d''appliquer le dé d''Inspiration bardique, mais elle doit se décider avant que le MD ne dise si le jet est un succès ou un échec. Une fois le dé d''Inspiration bardique lancé, il est consommé. Une créature ne peut avoir qu''un seul dé d''Inspiration bardique à la fois.\nVous pouvez utiliser cette capacité un nombre de fois égal à votre modificateur de Charisme (minimum 1). Vous regagnez vos dés d''Inspiration bardique après avoir terminé un repos long.','ACTION_BONUS',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-21 08:40:45','2016-11-21 08:40:45');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (171,'Inspiration bardique d8','Vous pouvez inspirer les autres en maniant les mots ou la musique. Pour ce faire, utilisez une action bonus à votre tour pour choisir une créature autre que vous-même dans un rayon de 18 mètres autour de vous et qui peut vous entendre. Cette créature gagne un dé d''Inspiration bardique (d8). Une fois dans les 10 minutes suivantes, la créature peut lancer le dé et ajouter le nombre obtenu à un jet de caractéristique, d''attaque ou de sauvegarde qu''elle vient de faire. La créature peut attendre de voir le résultat de jet de caractéristique, d''attaque ou de sauvegarde avant de décider d''appliquer le dé d''Inspiration bardique, mais elle doit se décider avant que le MD ne dise si le jet est un succès ou un échec. Une fois le dé d''Inspiration bardique lancé, il est consommé. Une créature ne peut avoir qu''un seul dé d''Inspiration bardique à la fois.\nVous pouvez utiliser cette capacité un nombre de fois égal à votre modificateur de Charisme (minimum 1). Vous regagnez vos dés d''Inspiration bardique après avoir terminé un repos long.','ACTION_BONUS',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-21 08:41:23','2016-11-21 08:41:23');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (172,'Inspiration bardique d10','Vous pouvez inspirer les autres en maniant les mots ou la musique. Pour ce faire, utilisez une action bonus à votre tour pour choisir une créature autre que vous-même dans un rayon de 18 mètres autour de vous et qui peut vous entendre. Cette créature gagne un dé d''Inspiration bardique (d10). Une fois dans les 10 minutes suivantes, la créature peut lancer le dé et ajouter le nombre obtenu à un jet de caractéristique, d''attaque ou de sauvegarde qu''elle vient de faire. La créature peut attendre de voir le résultat de jet de caractéristique, d''attaque ou de sauvegarde avant de décider d''appliquer le dé d''Inspiration bardique, mais elle doit se décider avant que le MD ne dise si le jet est un succès ou un échec. Une fois le dé d''Inspiration bardique lancé, il est consommé. Une créature ne peut avoir qu''un seul dé d''Inspiration bardique à la fois.\nVous pouvez utiliser cette capacité un nombre de fois égal à votre modificateur de Charisme (minimum 1). Vous regagnez vos dés d''Inspiration bardique après avoir terminé un repos long.','ACTION_BONUS',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-21 08:41:51','2016-11-21 08:41:51');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (173,'Inspiration bardique d12','Vous pouvez inspirer les autres en maniant les mots ou la musique. Pour ce faire, utilisez une action bonus à votre tour pour choisir une créature autre que vous-même dans un rayon de 18 mètres autour de vous et qui peut vous entendre. Cette créature gagne un dé d''Inspiration bardique (d12). Une fois dans les 10 minutes suivantes, la créature peut lancer le dé et ajouter le nombre obtenu à un jet de caractéristique, d''attaque ou de sauvegarde qu''elle vient de faire. La créature peut attendre de voir le résultat de jet de caractéristique, d''attaque ou de sauvegarde avant de décider d''appliquer le dé d''Inspiration bardique, mais elle doit se décider avant que le MD ne dise si le jet est un succès ou un échec. Une fois le dé d''Inspiration bardique lancé, il est consommé. Une créature ne peut avoir qu''un seul dé d''Inspiration bardique à la fois.\nVous pouvez utiliser cette capacité un nombre de fois égal à votre modificateur de Charisme (minimum 1). Vous regagnez vos dés d''Inspiration bardique après avoir terminé un repos long.','ACTION_BONUS',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-21 08:42:36','2016-11-21 08:42:36');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (174,'Chant de repos d8','Vous pouvez utiliser de la musique ou une oraison apaisante lors d''un repos court pour aider à revitaliser vos alliés blessés. Si vous ou toute créature amie qui peut entendre votre représentation récupérez des points de vie à la fin d''un repos court, chacune de ces créatures récupère 1d8 points de vie supplémentaires.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-21 09:01:53','2016-11-21 09:01:53');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (175,'Chant de repos d10','Vous pouvez utiliser de la musique ou une oraison apaisante lors d''un repos court pour aider à revitaliser vos alliés blessés. Si vous ou toute créature amie qui peut entendre votre représentation récupérez des points de vie à la fin d''un repos court, chacune de ces créatures récupère 1d10 points de vie supplémentaires.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-21 09:02:36','2016-11-21 09:02:36');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (176,'Chant de repos d12','Vous pouvez utiliser de la musique ou une oraison apaisante lors d''un repos court pour aider à revitaliser vos alliés blessés. Si vous ou toute créature amie qui peut entendre votre représentation récupérez des points de vie à la fin d''un repos court, chacune de ces créatures récupère 1d12 points de vie supplémentaires.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,NULL,1,'2016-11-21 09:03:32','2016-11-21 09:03:32');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (177,'Renvoi de morts-vivants 2','Au prix d''une action, vous présentez votre symbole sacré en psalmodiant une prière contre les morts-vivants. Chaque mort-vivant qui peut vous voir ou vous entendre et qui se trouve à 30 pieds ou moins de vous doit effectuer un jet de sauvegarde de Sagesse. Si la créature rate son jet de sauvegarde, elle est renvoyée pendant 1 minute ou jusqu''à ce qu''elle prenne des dégâts.\n\nUne créature renvoyée doit passer ses actions à essayer de s''éloigner le plus loin possible de vous et ne peut volontairement se rapprocher à moins de 30 pieds de vous. Elle ne peut pas avoir de réactions et peut utiliser ses actions seulement pour courir ou essayer de s''échapper d''un effet immobilisant. S''il n''y a nulle part où aller, la créature peut utiliser l''action d''esquive.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',78,1,'2016-11-21 19:58:27','2016-11-21 19:58:27');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (178,'Renvoi de morts-vivants 3','Au prix d''une action, vous présentez votre symbole sacré en psalmodiant une prière contre les morts-vivants. Chaque mort-vivant qui peut vous voir ou vous entendre et qui se trouve à 30 pieds ou moins de vous doit effectuer un jet de sauvegarde de Sagesse. Si la créature rate son jet de sauvegarde, elle est renvoyée pendant 1 minute ou jusqu''à ce qu''elle prenne des dégâts.\n\nUne créature renvoyée doit passer ses actions à essayer de s''éloigner le plus loin possible de vous et ne peut volontairement se rapprocher à moins de 30 pieds de vous. Elle ne peut pas avoir de réactions et peut utiliser ses actions seulement pour courir ou essayer de s''échapper d''un effet immobilisant. S''il n''y a nulle part où aller, la créature peut utiliser l''action d''esquive.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,3,'SHORT',177,1,'2016-11-21 19:59:18','2016-11-21 19:59:18');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (179,'Destruction de morts-vivants CR 1','Quand un mort-vivant rate son jet de sauvegarde contre votre capacité de Renvoi de morts-vivants, la créature est immédiatement détruite si son CR est égal ou inférieur à 1.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,79,1,'2016-11-21 20:01:38','2016-11-21 20:01:38');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (180,'Destruction de morts-vivants CR 2','Quand un mort-vivant rate son jet de sauvegarde contre votre capacité de Renvoi de morts-vivants, la créature est immédiatement détruite si son CR est égal ou inférieur à 2','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,179,1,'2016-11-21 20:02:16','2016-11-21 20:02:16');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (181,'Destruction de morts-vivants CR 3','Quand un mort-vivant rate son jet de sauvegarde contre votre capacité de Renvoi de morts-vivants, la créature est immédiatement détruite si son CR est égal ou inférieur à 3.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,180,1,'2016-11-21 20:02:44','2016-11-21 20:02:44');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (182,'Destruction de morts-vivants CR 4','Quand un mort-vivant rate son jet de sauvegarde contre votre capacité de Renvoi de morts-vivants, la créature est immédiatement détruite si son CR est égal ou inférieur à 4','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,181,1,'2016-11-21 20:03:15','2016-11-21 20:03:15');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (183,'Intervention divine améliorée','Vous pouvez faire appel à votre divinité pour qu''elle intervienne en cas de besoin réel. Implorer l''aide de votre divinité vous oblige à utiliser votre action. Décrivez l''aide que vous recherchez et l''appel à votre divinité réussit automatiquement. Le MD choisit la nature de l''intervention; l''effet d''un sort de clerc ou d''un sort de domaine est approprié. Vous ne pouvez plus utiliser cette capacité durant les 7 prochains jours.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,1,'LONG',80,1,'2016-11-21 20:06:55','2016-11-21 20:06:55');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (184,'Canalisation divine : préservation de vie 2','Vous pouvez utiliser votre Canalisation divine pour soigner des blessures graves. Au prix d''une action, vous présentez votre symbole sacré et invoquez une énergie curative qui redonne un montant de points de vie égal à 5 fois votre niveau de clerc. Choisissez une ou plusieurs créatures à 30 pieds ou moins de vous, et divisez ces points entre elles. Cette capacité ne peut pas ramener une créature à plus de 50% de ses points de vie maximums, et elle ne fonctionne pas sur les morts-vivants et les créatures artificielles.  Vous pouvez utiliser ce don 2 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',83,2,'2016-11-21 20:09:16','2016-11-21 20:18:01');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (185,'Canalisation divine : préservation de vie 3','Vous pouvez utiliser votre Canalisation divine pour soigner des blessures graves. Au prix d''une action, vous présentez votre symbole sacré et invoquez une énergie curative qui redonne un montant de points de vie égal à 5 fois votre niveau de clerc. Choisissez une ou plusieurs créatures à 30 pieds ou moins de vous, et divisez ces points entre elles. Cette capacité ne peut pas ramener une créature à plus de 50% de ses points de vie maximums, et elle ne fonctionne pas sur les morts-vivants et les créatures artificielles.  Vous pouvez utiliser ce don 3 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,3,'SHORT',184,2,'2016-11-21 20:09:58','2016-11-21 20:18:08');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (186,'Frappe divine (vie 2)','Vous gagnez la possibilité d’imprégner votre arme d''énergie divine. Une fois par tour, lorsque vous touchez une créature avec une attaque armée, vous pouvez infliger 2d8 de dégâts supplémentaires de type radiant à la cible.','PASSIVE',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,85,1,'2016-11-21 20:12:53','2016-11-21 20:12:53');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (187,'Canalisation divine : connaissance des âges 2','Vous pouvez utiliser votre Canalisation divine pour tirer profit d’un puits de connaissance divin. Au prix d’une action, vous choisissez une compétence ou un outil. Pendant 10 minutes, vous obtenez la maîtrise de la compétence ou de l’outil choisi.  Vous pouvez utiliser ce don 2 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',88,2,'2016-11-21 20:15:24','2016-11-21 20:17:39');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (188,'Canalisation divine : connaissance des âges 3','Vous pouvez utiliser votre Canalisation divine pour tirer profit d’un puits de connaissance divin. Au prix d’une action, vous choisissez une compétence ou un outil. Pendant 10 minutes, vous obtenez la maîtrise de la compétence ou de l’outil choisi.  Vous pouvez utiliser ce don 3 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,3,'SHORT',187,2,'2016-11-21 20:16:26','2016-11-21 20:17:46');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (189,'Canalisation divine : invocation de duplicité 2','Vous pouvez utiliser votre Canalisation divine pour créer une copie illusoire de vous-même. Par une action, vous créez une illusion parfaite de vous-même qui dure pendant 1 minute, ou jusqu''à ce que vous perdiez votre concentration (comme si vous étiez concentré sur un sort). L''illusion apparaît dans un espace inoccupé que vous pouvez voir dans un rayon de 30 pieds autour de vous. Par une action bonus à votre tour, vous pouvez déplacer l''illusion jusqu''à 30 pieds, dans un espace que vous pouvez, et sans l''éloigner de plus de 120 pieds de vous. Durant ce temps, vous pouvez lancer des sorts comme si vous étiez dans l''espace de l''illusion, mais vous devez utiliser vos propres sens. En outre, lorsque vous et votre illusion êtes à 5 pieds mètre ou moins d''une créature qui peut voir l''illusion, vous avez l''avantage aux jets d''attaque contre cette créature, l''illusion distrayant la cible.  Vous pouvez utiliser ce don 2 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',93,1,'2016-11-21 20:20:21','2016-11-21 20:20:21');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (190,'Canalisation divine : invocation de duplicité 3','Vous pouvez utiliser votre Canalisation divine pour créer une copie illusoire de vous-même. Par une action, vous créez une illusion parfaite de vous-même qui dure pendant 1 minute, ou jusqu''à ce que vous perdiez votre concentration (comme si vous étiez concentré sur un sort). L''illusion apparaît dans un espace inoccupé que vous pouvez voir dans un rayon de 30 pieds autour de vous. Par une action bonus à votre tour, vous pouvez déplacer l''illusion jusqu''à 30 pieds, dans un espace que vous pouvez, et sans l''éloigner de plus de 120 pieds de vous. Durant ce temps, vous pouvez lancer des sorts comme si vous étiez dans l''espace de l''illusion, mais vous devez utiliser vos propres sens. En outre, lorsque vous et votre illusion êtes à 5 pieds mètre ou moins d''une créature qui peut voir l''illusion, vous avez l''avantage aux jets d''attaque contre cette créature, l''illusion distrayant la cible.  Vous pouvez utiliser ce don 3 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,3,'SHORT',189,2,'2016-11-21 20:20:56','2016-11-21 20:21:12');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (191,'Canalisation divine : manteau d''ombres 2','Vous pouvez utiliser votre Canalisation divine pour disparaître. Par une action, vous devenez invisible jusqu''à la fin de votre prochain tour. Vous redevenez visible si vous attaquez ou lancez un sort. Vous pouvez utiliser ce don 3 fois avant un repose court ou long.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,3,'SHORT',94,2,'2016-11-21 20:23:05','2016-11-21 20:28:53');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (193,'Canalisation divine : lecture des pensées 2','Vous pouvez utiliser votre Canalisation divine pour lire les pensées d’une créature. Vous pouvez ensuite avoir accès à l’esprit de la créature et la commander. Par une action, choisissez une créature que vous pouvez voir située à 18 mètres ou moins de vous. La créature doit effectuer un jet de sauvegarde de Sagesse. Si la créature le réussi, vous ne pouvez réutiliser cette capacité sur la créature qu’après avoir terminé un repos long. Si la créature échoue, vous pouvez lire la surface de ses pensées (celles les plus en vue dans son esprit, représentant ses émotions et ce à quoi elle pense actuellement) lorsqu''elle est à 60 pieds ou moins de vous. Cet effet dure une minute. Pendant cette durée, vous pouvez utiliser votre action pour mettre fin à cet effet et lancer le sort suggestion à la créature sans consommer d’emplacement de sort. La créature échoue automatiquement son jet de sauvegarde contre ce sort.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,3,'LONG',89,2,'2016-11-21 20:30:01','2016-11-21 20:31:17');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (194,'Canalisation divine : coup au but 2','Vous pouvez utiliser votre Canalisation divine pour frapper avec une grande précision. Lorsque vous faites un jet d’attaque, vous pouvez utilisez votre Canalisation divine pour gagner un bonus de +10 au jet. ','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',99,1,'2016-11-21 20:34:10','2016-11-21 20:34:10');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (195,'Canalisation divine : coup au but 3','Vous pouvez utiliser votre Canalisation divine pour frapper avec une grande précision. Lorsque vous faites un jet d’attaque, vous pouvez utilisez votre Canalisation divine pour gagner un bonus de +10 au jet. ','ACTION',1,NULL,'NONE',NULL,NULL,NULL,3,'SHORT',194,1,'2016-11-21 20:34:40','2016-11-21 20:34:40');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (196,'Canalisation divine : bénédiction du dieu de la Guerre 2','Lorsqu''une créature dans un rayon de 30 pieds autour de vous fait un jet d’attaque, vous pouvez utiliser votre réaction pour faire bénéficier cette créature d’un bonus de +10 à son jet, en utilisant votre Canalisation divine.','REACTION',1,NULL,'NONE',NULL,NULL,NULL,3,'SHORT',100,1,'2016-11-21 20:36:42','2016-11-21 20:36:42');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (197,'Frappe divine (guerre 2)','Vous gagnez la possibilité d’imprégner votre arme d’énergie divine. Une fois à chacun de vos tours, lorsque vous touchez une créature avec une attaque d''arme, vous pouvez infliger 2d8 de dégâts supplémentaires du même type que celui de votre arme.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,NULL,NULL,101,1,'2016-11-21 20:38:36','2016-11-21 20:38:36');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (198,'Canalisation divine : radiance de l''aube 2','Vous pouvez utiliser votre Canalisation divine pour exploiter la lumière du soleil, bannissant les ténèbres et infligeant des dégâts radiants à vos ennemis. Par une action, vous présentez votre symbole sacré et toutes les ténèbres magiques dans un rayon de 30 pieds autour de vous sont dissipées. En outre, chaque créature hostile dans un rayon de 30 pieds autour de vous doit réaliser un jet de sauvegarde de Constitution. Une créature prend 2d10 + votre niveau de clerc de dégâts radiants en cas d''échec, ou la moitié de ces dégâts en cas de réussite. Une créature qui dispose d''une couverture totale vis à vis de vous n''est pas affectée.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,2,'SHORT',105,1,'2016-11-21 21:11:19','2016-11-21 21:11:19');
INSERT INTO feat (id, name, description, featUsage, isClassFeature, parentId, prerequisiteType, prerequisiteArmorProficiency, prerequisiteAbilityId, prerequisiteAbilityScore, nbUse, restType, replaceFeatId, version, created, updated) VALUES (199,'Canalisation divine : radiance de l''aube 3','Vous pouvez utiliser votre Canalisation divine pour exploiter la lumière du soleil, bannissant les ténèbres et infligeant des dégâts radiants à vos ennemis. Par une action, vous présentez votre symbole sacré et toutes les ténèbres magiques dans un rayon de 30 pieds autour de vous sont dissipées. En outre, chaque créature hostile dans un rayon de 30 pieds autour de vous doit réaliser un jet de sauvegarde de Constitution. Une créature prend 2d10 + votre niveau de clerc de dégâts radiants en cas d''échec, ou la moitié de ces dégâts en cas de réussite. Une créature qui dispose d''une couverture totale vis à vis de vous n''est pas affectée.','ACTION',1,NULL,'NONE',NULL,NULL,NULL,3,'SHORT',198,1,'2016-11-21 21:11:52','2016-11-21 21:11:52');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (1,'TOOL','Chalemie','La chalemie est un instrument à vent à anche double qui est considéré comme l''ancêtre direct du hautbois. Comme celui-ci, la chalemie a une perce conique; mais cette perce, ainsi que le pavillon et les trous sont plus larges et l''instrument comporte un disque en bois. Destiné à être jouée en plein air, la chalemie possède une sonorité puissante.',1,0,1,1,2,1,'2016-09-14 14:15:25','2016-09-14 14:15:25');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (2,'TOOL','Luth','Instrument à cordes pincées, à caisse de résonance en forme de demi-poire; dont le manche est en deux parties qui se coupent à angle droit.',2,0,1,1,35,1,'2016-09-14 14:17:08','2016-09-14 14:17:08');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (3,'TOOL','Flute','La flûte est un instrument à vent qui comporte huit trous de jeu, dont un manipulé par le pouce pour permettre l''émission des octaves aiguës.',1,0,1,1,2,1,'2016-09-14 14:20:58','2016-09-14 14:20:58');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (4,'TOOL','Cornemuse','La cornemuse est un instrument de musique à vent et plus particulièrement à anches qui se compose d''au moins deux tuyaux à anche simple ou double. Les anches sont mises en mouvement par une alimentation d''air provenant de la pression du bras de l''instrumentiste sur un réservoir taillé dans une peau.',6,0,1,1,30,1,'2016-09-14 14:25:05','2016-09-14 14:25:05');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (5,'TOOL','Lyre','La lyre est un instrument à corde. Elle n''a pas de manche, comme la guitare, dans le prolongement de sa caisse de résonance, mais deux bras flanqués de part et d''autre. La lyre fait partie de la famille de la harpe : les cordes sont verticales, elles se pincent à vide et ne produisent qu''un son unique. À la différence de la harpe, la lyre a des cordes d''égales longueurs qui forment un léger rayonnement. C''est la tension et la grosseur de la corde qui permet les différentes intonations. Contrairement à la harpe, les cordes sont aussi parallèles aux deux montants.',2,0,1,1,30,1,'2016-09-14 14:27:39','2016-09-14 14:27:39');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (6,'TOOL','Viole','La viole est un instrument de musique à 6 cordes jouées à l’aide d''un archet.',1,0,1,1,30,1,'2016-09-14 14:30:10','2016-09-14 14:30:10');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (7,'TOOL','Tympanon','Le tympanon est un instrument de musique de la famille des cithares sur table qui se présente sous la forme d''une caisse trapézoïdale. Un certain nombre de cordes, parfois réunies en divers chœurs, sont tendues sur la table d''harmonie. Le son est produit en frappant les cordes à l''aide de mailloches.',10,0,1,1,25,1,'2016-09-14 14:32:23','2016-09-14 14:32:23');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (8,'TOOL','Flute de pan','La flûte de Pan est un instrument de musique composé d''un ensemble de tuyaux sonores assemblés.',1,0,1,1,2,1,'2016-09-14 14:33:26','2016-09-14 14:33:26');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (9,'TOOL','Tambour','Un tambour est un instrument de musique à percussion constitué d''un fût sur lequel sont tendues une ou plusieurs peaux, frappées à l''aide des doigts ou de baguettes prévues à cet effet. La vibration ainsi obtenue est amplifiée par le fût qui fait office de caisse de résonance.',3,0,1,1,6,1,'2016-09-14 14:35:00','2016-09-14 14:35:00');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (10,'TOOL','Dés','Le jeu de dés se pratique avec les dés, des objets le plus souvent cubiques dont les 6 faces sont habituellement numérotées de 1 à 6.',0.1,0,1,1,1,1,'2016-09-14 14:38:37','2016-09-14 14:38:37');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (11,'TOOL','Cartes à jouer','Le jeu comprend 52 cartes à jouer, réparties suivant treize valeurs sur quatre enseignes.',0.1,0,1,1,1,1,'2016-09-14 14:40:56','2016-09-14 14:40:56');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (12,'TOOL','Jeu d''échecs','Le jeu d''échecs se compose d’un plateau ou tablier appelé échiquier composé de soixante-quatre cases claires et sombres nommées les cases blanches et les cases noires. Les joueurs jouent à tour de rôle en déplaçant l''une de leurs seize pièces, claires pour le camp des blancs, sombres pour le camp des noirs. Chaque joueur possède au départ un roi, une dame, deux tours, deux fous, deux cavaliers et huit pions.',0.5,0,1,1,2,1,'2016-09-14 14:44:14','2016-09-14 14:44:14');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (13,'TOOL','Kit d''empoisonneur','Ce kit comprend les flacons, produits chimiques et autres appareils nécessaires à la création de poisons. Si vous maîtrisez le kit d''empoisonneur, vous ajoutez votre bonus de maîtrise à tous les jets de caractéristique pour créer et manipuler des poisons.',2,0,1,1,50,2,'2016-09-14 14:45:25','2016-09-14 14:46:22');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (14,'TOOL','Kit d''herboriste','Ce kit contient une variété d''instruments, tels des pinces, un pilon, des sachets et des flacons que les herboristes utilisent pour créer des remèdes et des potions. Si vous maîtrisez le kit d''herboriste, vous ajoutez votre bonus de maîtrise à tous les jets de caractéristique pour identifier des herbes ou concocter des mélanges et des infusions. Maîtriser le kit d''herboriste est indispensable pour créer des potions de soins et des antidotes.',3,0,1,1,5,1,'2016-09-14 14:46:15','2016-09-14 14:46:15');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (15,'TOOL','Kit de contrefaçon','Cette petite boîte contient une variété de papiers et parchemins, plumes et encres, sceaux et cire à cacheter, feuilles d''argent et d''or, et autres fournitures nécessaires à la création de faux documents physiques convaincants. La maîtrise de ce kit vous permet d''ajouter votre bonus de maîtrise à tous les jets de caractéristique pour créer un faux d''un document physique.',5,0,1,1,15,1,'2016-09-14 14:47:07','2016-09-14 14:47:07');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (16,'TOOL','Kit de déguisement','Ce kit comprend des produits cosmétiques, de la teinture pour les cheveux et de petits accessoires pour vous permettre de créer des déguisements qui changent votre apparence physique. Si vous maîtrisez le kit de déguisement, vous ajoutez votre bonus de maîtrise à tous les jets de caractéristiques pour créer un déguisement visuel.',3,0,1,1,25,2,'2016-09-14 14:47:45','2016-09-14 14:49:21');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (17,'TOOL','Outils de navigation','Ces outils sont utilisés pour la navigation en mer. La maîtrise des outils de navigation vous permet de tracer le parcours d''un navire et de suivre des cartes de navigation. En outre, ces outils vous permettent d''ajouter votre bonus de maîtrise à tous les jets de caractéristique pour éviter de se perdre en mer.',2,0,1,1,25,1,'2016-09-14 14:48:20','2016-09-14 14:48:20');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (18,'TOOL','Outils de voleur','Ces outils comprennent une petite lime, divers outils de crochetage, un petit miroir monté sur une poignée en métal, une paire de ciseaux à lames fines et une paire de pinces. Si vous maîtrisez les outils de voleur, vous pouvez ajouter votre bonus de maîtrise à tous les jets de caractéristique pour désactiver un piège ou crocheter une serrure.',1,0,1,1,25,2,'2016-09-14 14:48:57','2016-09-14 14:49:09');
INSERT INTO equipment (id, type, name, description, weight, isMagical, isPurchasable, isSellable, basePrice, version, created, updated) VALUES (19,'TOOL','Matériel d''alchimie',NULL,8,0,1,1,50,1,'2016-09-14 14:50:19','2016-09-14 14:50:19');
