INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (1,'Jet d''acide','Vous propulsez une bulle d''acide. Choisissez une ou deux créatures, lesquelles sont à 5 pieds ou moins l''une de l''autre, dans la portée du sort. La cible doit réussir un jet de sauvegarde de Dextérité sans quoi elle subit 1d6 de dégâts d''acide. \nLes dégâts de ce sort augmentent de 1d6 lorsque vous atteignez le niveau 5 (2d6), le niveau 11 (3d6) et le niveau 17 (4d6). ',0,'EVOCATION','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'CREATURE',NULL,'DISTANCE',60,2,0,1,2,'2016-09-16 08:47:01','2016-09-16 08:52:56');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (2,'Contact glacial','Vous créez une main squelettique fantomatique dans l''espace d''une créature à portée. Faites une attaque de sort à distance contre la créature pour l''étreindre d''une froideur sépulcrale. Si le coup touche, la cible subit 1d8 dégâts nécrotiques, et elle ne peut récupérer ses points de vie avant le début de votre prochain tour. Jusque là, la main fantomatique s''accroche à la cible. Si vous ciblez un mort-vivant, il aura également un désavantage à ses jets d''attaque contre vous jusqu''à la fin de votre prochain tour. \nLes dégâts de ce sort augmentent de 1d8 lorsque vous atteignez le niveau 5 (2d8), le niveau 11 (3d8) et le niveau 17 (4d8). ',0,'NECROMANCIE','ACTION',NULL,NULL,'TIME',NULL,'MINUTE','CREATURE',NULL,'DISTANCE',100,NULL,1,1,1,'2016-09-16 08:55:01','2016-09-16 08:55:01');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (3,'Coup au but','Vous tendez votre main et pointez un doigt en direction d''une cible à portée. Votre magie vous aide à trouver une petite faille dans les défenses de votre cible. Lors de votre prochain tour, vous obtenez l''avantage sur votre premier jet d''attaque contre la cible, à condition que le sort ne soit pas déjà terminé. ',0,'DIVINATION','ACTION',NULL,NULL,'CONCENTRATION',NULL,NULL,'CREATURE',NULL,'DISTANCE',30,NULL,0,0,1,'2016-09-16 08:57:34','2016-09-16 08:57:34');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (4,'Décharge électrique','La foudre jaillit de votre main pour délivrer un choc électrique à une créature que vous essayez de toucher. Effectuez une attaque de sort de corps à corps contre la cible. Vous avez l''avantage sur le jet d''attaque si la cible porte une armure en métal. En cas de réussite, la cible prend 1d8 dégâts de foudre, et elle ne peut pas prendre de réaction jusqu''au début de son prochain tour. \nLes dégâts du sort augmentent de 1d8 lorsque vous atteignez le niveau 5 (2d8), le niveau 11 (3d8) et le niveau 17 (4d8). ',0,'EVOCATION','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'CREATURE',NULL,'TOUCH',NULL,NULL,1,1,1,'2016-09-16 08:58:57','2016-09-16 08:58:57');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (5,'Décharge occulte','Un rayon d''énergie crépitante zigzague jusqu''à une créature à portée. Effectuez une attaque de sort à distance contre la cible. En cas de réussite, la cible subit 1d10 dégâts de force. \nCe sort crée plus d''un rayon lorsque vous montez en niveau : deux rayons au niveau 5, trois rayons au niveau 11, et quatre rayons au niveau 17. Vous pouvez diriger les rayons sur une cible unique et les répartir entre différentes créatures. Effectuez un jet d''attaque séparé pour chaque rayon. ',0,'EVOCATION','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'CREATURE',NULL,'DISTANCE',100,NULL,1,1,1,'2016-09-16 09:00:39','2016-09-16 09:00:39');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (6,'Druiderie','En murmurant aux esprits de la nature, vous créez l''un des effets suivants, dans la portée du sort : \n• Vous créez un minuscule et inoffensif effet sensoriel qui permet de prédire les conditions météorologiques pour les 24 prochaines heures, à l''endroit où vous êtes. L''effet pourrait se manifester comme un orbe doré pour indiquer un ciel dégagé, un nuage pour annoncer la pluie, un flocon de neige pour une chute de neige et ainsi de suite. L''effet persiste pour 1 round. \n• Vous provoquez immédiatement la floraison d''une fleur, l''ouverture d''une gousse ou le débourrement d''un bourgeon de feuille. \n• Vous créez un inoffensif effet sensoriel instantané comme une chute de feuilles, une légère brise, le son d''un petit animal ou une subtile odeur de putois. L''effet doit se limiter à un cube de 5 pieds mètre d''arête. \n• Vous éteignez ou allumez instantanément une chandelle, une torche ou un petit feu de camp. ',0,'TRANSMUTATION','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'OBJECT',NULL,'DISTANCE',30,NULL,0,0,1,'2016-09-16 09:02:11','2016-09-16 09:02:11');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (7,'Flamme','Une flamme vacillante apparaît dans votre main. La flamme y reste pour la durée du sort et n''endommage pas votre équipement, ni ne vous blesse. La flamme diffuse une lumière vive dans un rayon de 10 pieds et une lumière faible dans un rayon supplémentaire de 10 pieds. Le sort prend fin si vous l''annulez par une action ou si vous le lancez de nouveau. \nVous pouvez également attaquer avec la flamme, mettant ainsi un terme au sort. Lorsque vous incantez ce sort, ou en utilisant une action lors d''un tour suivant, vous pouvez lancer la flamme sur une créature située à 30 pieds de vous maximum. Faites une attaque de sort à distance. En cas de réussite, la cible subit 1d8 dégâts de feu. \nLes dégâts de ce sort augmentent de 1d8 lorsque vous atteignez le niveau 5 (2d8), le niveau 11 (3d8) et le niveau 17 (4d8). ',0,'EVOCATION','ACTION',NULL,NULL,'TIME',NULL,'MINUTE','CREATURE',NULL,'SELF',NULL,NULL,1,0,2,'2016-09-16 09:12:12','2016-09-16 09:13:54');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (8,'Flamme sacrée','Un rayonnement semblable à des flammes descend sur une créature que vous pouvez voir dans la portée du sort. La cible doit réussir un jet de sauvegarde de Dextérité ou subir 1d8 dégâts de type radiant. La cible ne gagne aucun bénéfice d''abri pour ce jet de sauvegarde. \nLes dégâts du sort augmentent de 1d8 lorsque vous atteignez le niveau 5 (2d8), le niveau 11 (3d8), et le niveau 17 (4d8). ',0,'EVOCATION','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'CREATURE',NULL,'DISTANCE',60,2,0,1,2,'2016-09-16 09:13:31','2016-09-16 09:14:05');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (9,'Gourdin magique','Le bois du gourdin que vous tenez est altéré par les pouvoirs de la nature. Pour la durée du sort, vous pouvez utiliser votre caractéristique pour lancer vos sorts plutôt que votre Force pour les jets d''attaque au corps à corps et de dégâts avec cette arme, et les dégâts de l''arme deviennent des d8. De plus, l''arme devient une arme magique, si ce n''est pas déjà le cas. Ce sort se termine si vous le lancez de nouveau ou si vous lâchez l''arme enchantée. ',0,'TRANSMUTATION','BONUS_ACTION',NULL,NULL,'TIME',NULL,'MINUTE','OBJECT',NULL,'TOUCH',NULL,NULL,0,0,2,'2016-09-16 09:16:31','2016-09-16 09:16:51');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (10,'Guide','Vous touchez une créature consentante. Une fois avant la fin du sort, la cible peut lancer un d4 et ajouter le résultat du dé à un jet de caractéristique de son choix. Elle peut lancer le dé avant ou après avoir effectué son jet de caractéristique. Le sort prend alors fin.',0,'DIVINATION','ACTION',NULL,NULL,'CONCENTRATION',NULL,NULL,'CREATURE',NULL,'TOUCH',NULL,NULL,0,0,1,'2016-09-16 09:19:21','2016-09-16 09:19:21');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (11,'Moquerie vicieuse','Vous vomissez un flot d''insultes entremêlées de subtils enchantements sur une créature à portée et que vous pouvez voir. Si la cible peut vous entendre (il n''est pas nécessaire qu''elle comprenne mais juste qu''elle devine votre intention), elle doit réussir un jet de sauvegarde de Sagesse ou subir 1d4 dégâts psychiques et avoir un désavantage au prochain jet d''attaque qu''elle fera avant la fin de son prochain tour. \nLes dégâts de ce sort augmentent de 1d4 lorsque vous atteignez le niveau 5 (2d4), le niveau 11 (3d4) et le niveau 17 (4d4). ',0,'ENCHANTEMENT','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'CREATURE',NULL,'DISTANCE',60,5,0,1,1,'2016-09-16 09:22:46','2016-09-16 09:22:46');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (12,'Prestidigitation','Ce sort est un tour de magie mineur que les lanceurs de sorts novices emploient comme exercice. Ce sort permet de provoquer l''un des effets magiques suivants : \n• Le sort crée instantanément un effet sensoriel inoffensif, comme une pluie d''étincelles, une bouffée d''air, de timides notes de musique, ou une étrange odeur. \n• Le sort allume ou éteint instantanément une bougie, torche ou un petit feu de camp. \n• Le sort nettoie ou souille instantanément un objet pas plus volumineux qu''un cube de 30 cm de côté. \n• Le sort réchauffe ou refroidit du matériel non vivant pouvant être contenu dans un cube de 30 cm de côté pendant une heure. \n• Le sort fait apparaître un symbole, une petite marque ou couleur sur un objet ou une surface pendant 1 heure. \n• Le sort permet de créer une babiole non magique ou une image illusoire qui peut tenir dans votre main et qui dure jusqu''à la fin de votre prochain tour. \nSi le sort est lancé plusieurs fois, il est possible de conserver actifs 3 de ces effets non instantanés simultanément, et il est possible de révoquer ces effets au prix d''une action. ',0,'TRANSMUTATION','ACTION',NULL,NULL,'TIME',NULL,'HOUR','OBJECT',NULL,'DISTANCE',10,NULL,0,0,1,'2016-09-16 09:25:13','2016-09-16 09:25:13');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (13,'Protection contre les armes','Vous tendez votre main et tracez un symbole de protection dans les airs. Jusqu''à la fin de votre prochain tour, vous obtenez la résistance contre les dégâts contondants, tranchants et perforants infligés par des attaques avec arme. ',0,'ABJURATION','ACTION',NULL,NULL,'TIME',NULL,'MINUTE','SELF',NULL,'SELF',NULL,NULL,0,0,1,'2016-09-16 09:30:10','2016-09-16 09:30:10');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (14,'Rayon de givre','Un faisceau frigide de lumière bleuâtre se dirige vers une créature dans la portée du sort. Effectuez une attaque de sort à distance contre la cible. S''il touche, la cible subit 1d8 dégâts de froid et sa vitesse est réduite de 10 pieds jusqu''à début de votre prochain tour de jeu. \nLes dégâts du sort augmentent de 1d8 lorsque vous atteignez le niveau 5 (2d8), le niveau 11 (3d8) et le niveau 17 (4d8). ',0,'EVOCATION','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'CREATURE',NULL,'DISTANCE',60,NULL,1,1,1,'2016-09-16 09:50:22','2016-09-16 09:50:22');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (15,'Stabilisation','Vous touchez une créature vivante qui est à 0 point de vie. La créature devient stable. Ce sort n''a aucun effet sur les morts-vivants ou les créatures artificielles. ',0,'NECROMANCIE','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'CREATURE',NULL,'TOUCH',NULL,NULL,0,0,1,'2016-09-16 10:00:13','2016-09-16 10:00:13');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (16,'Thaumaturgie','Vous simulez une chose extraordinaire, un signe de puissance surnaturelle. Vous créez un des effets magiques suivants dans la limite de portée du sort : \n• Votre voix devient trois fois plus puissante que la normale pour 1 minute. \n• Vous faîtes vaciller des flammes, augmentez ou diminuez leur intensité, ou bien encore vous changez leur couleur pendant 1 minute.\n• Vous causez des tremblements inoffensifs dans le sol pendant 1 minute. \n• Vous créez un son instantané qui provient d''un point de votre choix dans la limite de portée du sort, tel qu''un grondement de tonnerre, le cri d''un corbeau ou des chuchotements de mauvais augure. \n• Vous provoquez instantanément l''ouverture ou le claquement brusque d''une porte ou d''une fenêtre non verrouillée. \n• Vous altérez l''apparence de vos yeux pendant 1 minute. \nSi vous lancez ce sort plusieurs fois, vous pouvez avoir activement jusqu''à trois de ses effets à la fois, et vous pouvez rompre un effet au prix d''une action. ',0,'TRANSMUTATION','ACTION',NULL,NULL,'TIME',NULL,'MINUTE','OBJECT',NULL,'DISTANCE',30,NULL,0,0,1,'2016-09-16 10:02:49','2016-09-16 10:02:49');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (17,'Trait de feu','Vous lancez un trait de feu sur une créature ou un objet à portée. Faites une attaque de sort à distance contre la cible. En cas de réussite, la cible prend 1d10 dégâts de feu. Un objet inflammable touché par ce sort prend feu s''il n est pas porté. \nLes dégâts du sort augmentent de 1d10 aux niveaux 5 (2d10), 11 (3d10) et 17 (4d10). ',0,'EVOCATION','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'CREATURE',NULL,'DISTANCE',120,NULL,1,1,1,'2016-09-16 10:04:12','2016-09-16 10:04:12');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (18,'Vaporisation de poison','Vous tendez votre paume vers une créature visible dans la portée du sort et vous projetez une bouffée de gaz nocif de votre main. La créature doit réussir un jet de sauvegarde de Constitution sans quoi elle subit 1d12 de dégâts de poison. \nLes dégâts de ce sort augmentent de 1d12 lorsque vous atteignez le niveau 5 (2d12), le niveau 11 (3d12) et le niveau 17 (4d12). ',0,'EVOCATION','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'CREATURE',NULL,'DISTANCE',10,3,0,0,1,'2016-09-16 10:05:32','2016-09-16 10:05:32');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (19,'Liane épineuse','Vous créez une longue et épaisse liane ressemblant à de la vigne et recouverte d''épines qui, selon vos ordres, s''accroche à une créature à portée. Effectuez une attaque de sort de corps à corps contre la cible. Si l''attaque touche, la créature subit 1d6 dégâts perforants, et si la créature est de taille G ou inférieure, vous la tirez de 3 mètres vers vous. \nLes dégâts de ce sort augmentent de 1d6 lorsque vous atteignez le niveau 5 (2d6), le niveau 11 (3d6) et le niveau 17 (4d6). ',0,'TRANSMUTATION','ACTION',NULL,NULL,'INSTANTANEOUS',NULL,NULL,'CREATURE',NULL,'DISTANCE',30,NULL,1,1,3,'2016-11-10 20:20:01','2016-11-10 20:39:46');
INSERT INTO spell (id, name, description, level, school, castingTime, castingTimeValue, castingTimeUnit, duration, durationTimeValue, durationTimeUnit, target, areaOfEffect, rangeType, rangeValueInFeet, savingThrowAbilityId, attackRoll, higherLevel, version, created, updated) VALUES (20,'Manipulation à distance','Une main spectrale apparaît à un point précis choisi à portée. La main expire à la fin de la durée du sort ou si elle est révoquée au prix d''une action. La main disparaît si elle se retrouve à plus de 9 m du lanceur de sorts ou si ce sort est jeté une nouvelle fois. \nLe lanceur de sorts peut utiliser son action pour contrôler la main. La main peut manipuler un objet, ouvrir une porte ou un contenant non verrouillé, ranger ou récupérer un objet d''un contenant ouvert, ou bien verser le contenu d''une fiole. La main peut être déplacée jusqu''à 30 pieds à chaque fois que vous l''utilisez. \nLa main ne peut attaquer, activer des objets magiques ou transporter plus de 10 lbs. ',0,'EVOCATION','ACTION',NULL,NULL,'TIME',NULL,'MINUTE','POINT',NULL,'DISTANCE',30,NULL,0,0,1,'2016-11-11 14:53:17','2016-11-11 14:53:17');
